INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1046, '二つの顔を解く鍵　二人の未来へのヒント', '運命の扉を開く', '唯一無二の人の本当の本当の気持ちは？　運命の分岐点はどうなる？　内なる戦いをハワイアンタロットが占います。', 4, NULL, NULL, NULL, NULL, NULL, '2026-01-05 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1046, 104601, '二人の未来について'),
(1046, 104602, '率直な想いの意味と運命の終着点'),
(1046, 104603, '正直な心が示すもの'),
(1046, 104604, '求める相手にとっての決意すること'),
(1046, 104605, '心の真実と決断の結果の関係'),
