INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1074, '意中の人は本当に…？　真実の心と想いの行方', '恋の顛末予測', '想いの伝わりの真相を知りたい…　想い人の素の気持ちと恋の顛末を、ハワイアンタロットが解き明かします。', 2, NULL, NULL, NULL, NULL, NULL, '2026-02-02 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1074, 107401, '気になる人の二人の方向性に対する想い'),
(1074, 107402, '未来の関係の展望'),
(1074, 107403, '純粋な気持ちが示すもの'),
(1074, 107404, '恋の結末の可能性'),
(1074, 107405, '本当の想いの本質'),
(1074, 107406, '隠れた感情を深く知る'),
(1074, 107407, '片想いの行方を左右する要因'),
