INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1075, '先行きの様子が見える　あの人の率直な想い', '感情の本体を読む', '未来の動向が気になるあなたへ。本当の感情を含む心の本音を、ハワイアンタロットが完全鑑定。', 3, NULL, NULL, NULL, NULL, NULL, '2026-02-03 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1075, 107501, '適切な行動'),
(1075, 107502, '見守る相手が求める真意の把握'),
(1075, 107503, '先の見通しについて'),
(1075, 107504, '将来展望における気になる人の役割'),
(1075, 107505, '大切な人の魂の叫びと未来予想図'),
