INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1076, '運命の人との理性と感情　決めた未来完全予測', '運命の選択の答え', '忘れられない人との複雑な関係が明らかに。内なる愛と未来の姿を、ハワイアンタロットが徹底的に占います。', 4, NULL, NULL, NULL, NULL, NULL, '2026-02-04 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1076, 107601, '理解することがもたらす運命の最終形'),
(1076, 107602, '問うべきことがもたらす関係の行方'),
(1076, 107603, '愛と罪悪感を左右する要因'),
(1076, 107604, '唯一無二の人の視点から見た深い愛情'),
(1076, 107605, '決断の先における究極の選択'),
(1076, 107606, '理性と感情における決断すべきこと'),
(1076, 107607, '魂の叫びの深層'),
