INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1081, 108101, '二人の再会への復縁への意思'),
(1081, 108102, '関係の修復の核心'),
(1081, 108103, '心残りと復縁後の未来の関係'),
(1081, 108104, '絆の修復を左右する要因'),
(1081, 108105, '復縁の展望の展望'),
(1081, 108106, '受け入れること'),
(1081, 108107, 'もう一度の関係における以前の恋人の役割'),
