INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1102, '出会いの好機と前向きさ　縁の成立を引き寄せる', '恋愛運詳細ガイド', '出会いの運勢を解明し、縁の成立を予測。愛を探すあなたについて、ハワイアンタロットが特別な縁を占います。', 10, NULL, NULL, NULL, NULL, NULL, '2026-03-02 00:00:00');
