INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1103, 'これからの展開を占う　伴侶の内なる声', '詳細鑑定', '隠れた想いを解明し、二人の行方を予測。愛する人について、ハワイアンタロットが二人の未来を占います。', 1, NULL, NULL, NULL, NULL, NULL, '2026-03-03 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1103, 110301, '大事にすることの意味'),
(1103, 110302, '真実の愛の全体像'),
(1103, 110303, '心の奥と迎える明日の関係'),
(1103, 110304, '意識すべきことがもたらす運命の道筋'),
(1103, 110305, '将来の関係の可能性'),
(1103, 110306, 'その人が感じていること'),
(1103, 110307, '純粋な感情が示すもの'),
(1103, 110308, 'かけがえのない人が求める愛の深まり'),
(1103, 110309, '愛の成長の核心');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1103, 110301, 11030101, '出たカードは【HUNA】。可能性を禁じないことを示す重要なカードです。大事にすることの意味に秘められた真実は、秘密によって明らかになります。愛に関わるあらゆる疑問や不安は、秘密という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、秘密を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。秘密さえ忘れなければ、二人の未来は明るく輝くでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1103, 110301, 11030102, '【LEHUA】のカードが語りかけます。安心して前進するという智恵を、あなたに授けようとしています。大事にすることの意味において、イニシエーションが極めて重要な役割を果たしています。このイニシエーションのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。イニシエーションという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1103, 110301, 11030103, '【TAPA】のカードが示されました。「パートナーシップ、距離感を考える」というメッセージが込められています。あなたが知りたい大事にすることの意味の核心は、結合にあります。結合というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。結合を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1103, 110301, 11030104, '【HINA】のカードです。HINAが示すのは養いであり、自分自身を成長させることの重要性です。大事にすることの意味に関して、カードは養いという重要なメッセージを伝えています。あなたが今必要としているのは、この養いを深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。養いの真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1103, 110302, 11030201, '【MENEHUNE】のカードです。MENEHUNEが示すのは持続であり、コツコツと積み重ねることの重要性です。真実の愛の全体像において、持続が極めて重要な役割を果たしています。この持続のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。持続という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1103, 110302, 11030202, '【MAUI】のカードが語りかけます。失敗を恐れずチャレンジするという智恵を、あなたに授けようとしています。真実の愛の全体像に秘められた真実は、トリックスターによって明らかになります。愛に関わるあらゆる疑問や不安は、トリックスターという視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、トリックスターを中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。トリックスターさえ忘れなければ、二人の未来は明るく輝くでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110302, 11030203, 'カードに現れたのは【MO’O】。テーマは「不安が成功を妨げる」です。真実の愛の全体像において最も大切なのは、罠という概念を正しく認識することです。罠は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて罠と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、罠と共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1103, 110302, 11030204, '【IWI】のカードが出ました。このカードは「自分も他人も大切にする」を意味します。真実の愛の全体像に対するハワイアンタロットの答えは明確です。それは尊敬です。この尊敬という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。尊敬は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で尊敬を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1103, 110303, 11030301, 'タロットが告げるのは【PAPA&WAKEA】。苦闘・全体というキーワードで、葛藤の中から新しいものを生み出すことを伝えています。カードが示す心の奥と迎える明日の関係の答えは、苦闘・全体にあります。この苦闘・全体を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、苦闘・全体という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、苦闘・全体の導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1103, 110303, 11030302, '【POLI’AHU】のカードが示されました。「不動の静けさからくる強さ」というメッセージが込められています。心の奥と迎える明日の関係に秘められた真実は、静けさの愛撫によって明らかになります。愛に関わるあらゆる疑問や不安は、静けさの愛撫という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、静けさの愛撫を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。静けさの愛撫さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1103, 110303, 11030303, '【I’O】のカードです。I’Oが示すのはミステリーであり、全ては必然で起こっていることの重要性です。恋愛における心の奥と迎える明日の関係を占う上で、ミステリー以上に重要なものはありません。ミステリーは、二人の関係を支える基盤であり、愛を育む土壌です。このミステリーを大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、ミステリーがあれば必ず乗り越えられます。愛を信じ、ミステリーと共に歩んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1103, 110303, 11030304, 'カードに現れたのは【LAMAKU】。テーマは「意思の力で喜びを手にする」です。心の奥と迎える明日の関係に秘められた真実は、勝利によって明らかになります。愛に関わるあらゆる疑問や不安は、勝利という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、勝利を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。勝利さえ忘れなければ、二人の未来は明るく輝くでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1103, 110304, 11030401, 'タロットが告げるのは【POHAKU】。関係というキーワードで、人間関係を見直すことを伝えています。意識すべきことがもたらす運命の道筋に秘められた真実は、関係によって明らかになります。愛に関わるあらゆる疑問や不安は、関係という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、関係を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。関係さえ忘れなければ、二人の未来は明るく輝くでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110304, 11030402, '【PUEO】のカードが示されました。「自然の流れに身をまかせる」というメッセージが込められています。意識すべきことがもたらす運命の道筋に対するハワイアンタロットの答えは明確です。それは導きです。この導きという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。導きは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で導きを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1103, 110304, 11030403, '【LONO/KAMAPUA’A】のカードが語りかけます。やり切ることで収穫に繋がるという智恵を、あなたに授けようとしています。意識すべきことがもたらす運命の道筋に秘められた真実は、収穫/豊穣によって明らかになります。愛に関わるあらゆる疑問や不安は、収穫/豊穣という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、収穫/豊穣を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。収穫/豊穣さえ忘れなければ、二人の未来は明るく輝くでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110304, 11030404, '【ALOHA】のカードが出ました。このカードは「真の愛を学ぶ」を意味します。意識すべきことがもたらす運命の道筋を考える上で、愛は欠かせない要素となっています。愛の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、愛の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、愛のメッセージを受け取りましょう。道は必ず開けます。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1103, 110305, 11030501, '【KANALOA】のカードが現れました。基盤を象徴し、基盤づくりをすることを教えてくれます。将来の関係の可能性において、基盤が極めて重要な役割を果たしています。この基盤のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。基盤という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1103, 110305, 11030502, 'タロットが告げるのは【PO/AO】。無意識/意識というキーワードで、深層心理を探ることを伝えています。カードが示す将来の関係の可能性の答えは、無意識/意識にあります。この無意識/意識を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、無意識/意識という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、無意識/意識の導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1103, 110305, 11030503, '出たカードは【HO’OPONOPONO】。コミュニケーションを大切にすることを示す重要なカードです。カードが示す将来の関係の可能性の答えは、コミュニケーションにあります。このコミュニケーションを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、コミュニケーションという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、コミュニケーションの導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110305, 11030504, 'カードに現れたのは【TI】。テーマは「感情、肉体、精神の浄化をする」です。将来の関係の可能性に対するハワイアンタロットの答えは明確です。それは浄化です。この浄化という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。浄化は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で浄化を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1103, 110306, 11030601, '【PUA】が選ばれました。「目的を定め前進あるのみ」がこのカードの核心的メッセージです。あなたが知りたいその人が感じていることの核心は、前進にあります。前進というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。前進を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1103, 110306, 11030602, '【HULU】のカードです。HULUが示すのはイリュージョンであり、無限の可能性ことの重要性です。その人が感じていることを考える上で、イリュージョンは欠かせない要素となっています。イリュージョンの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、イリュージョンの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、イリュージョンのメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110306, 11030603, '【NAUPAKA】のカードが示されました。「切り離す」というメッセージが込められています。恋愛におけるその人が感じていることを占う上で、別離以上に重要なものはありません。別離は、二人の関係を支える基盤であり、愛を育む土壌です。この別離を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、別離があれば必ず乗り越えられます。愛を信じ、別離と共に歩んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1103, 110306, 11030604, '【ANUENUE】のカードです。ANUENUEが示すのは変容であり、隠れた部分に光をあてることの重要性です。タロットが告げるその人が感じていることに関する真実は、変容に集約されています。変容という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。変容を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110307, 11030701, '【AWA】のカードが語りかけます。無条件と見返りの違いを意識するという智恵を、あなたに授けようとしています。純粋な感情が示すものを考える上で、供物・生贄は欠かせない要素となっています。供物・生贄の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、供物・生贄の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、供物・生贄のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1103, 110307, 11030702, 'カードに現れたのは【HAUMEA】。テーマは「必要なものは与えられる」です。純粋な感情が示すものを考える上で、滋養は欠かせない要素となっています。滋養の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、滋養の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、滋養のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1103, 110307, 11030703, '【PELE】のカードが語りかけます。情熱を注ぐという智恵を、あなたに授けようとしています。純粋な感情が示すものを考える上で、隆起は欠かせない要素となっています。隆起の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、隆起の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、隆起のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1103, 110307, 11030704, 'タロットが告げるのは【MANA】。力を与えることというキーワードで、満ちているエネルギーをどう使うかことを伝えています。カードが示す純粋な感情が示すものの答えは、力を与えることにあります。この力を与えることを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、力を与えることという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、力を与えることの導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1103, 110308, 11030801, '【OLE】が選ばれました。「言葉を大切にする」がこのカードの核心的メッセージです。かけがえのない人が求める愛の深まりを考える上で、物語は欠かせない要素となっています。物語の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、物語の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、物語のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1103, 110308, 11030802, 'ハワイアンタロットの【MANO】。リーダーシップのエネルギーを持ち、リーダーシップを取ることを説いています。あなたのかけがえのない人が求める愛の深まりには、リーダーシップが深く関わっています。このリーダーシップというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。リーダーシップのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、リーダーシップと共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110308, 11030803, '【KU/HINA】のカードが示されました。「活動する/休息する」というメッセージが込められています。あなたのかけがえのない人が求める愛の深まりには、戦い/癒しが深く関わっています。この戦い/癒しというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。戦い/癒しのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、戦い/癒しと共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1103, 110308, 11030804, '【LAKA】のカードです。LAKAが示すのはインスピレーションであり、直感を大切にすることの重要性です。かけがえのない人が求める愛の深まりに対するハワイアンタロットの答えは明確です。それはインスピレーションです。このインスピレーションという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。インスピレーションは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でインスピレーションを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110309, 11030901, '【MAMALAHOE】のカードが現れました。同情を象徴し、自分の気持ちを最優先することを教えてくれます。あなたが知りたい愛の成長の核心の核心は、同情にあります。同情というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。同情を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1103, 110309, 11030902, 'カードに現れたのは【WA’A】。テーマは「大きな変化が訪れる」です。あなたの愛の成長の核心には、移動が深く関わっています。この移動というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。移動のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、移動と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1103, 110309, 11030903, '【ANO’ANO】のカードが語りかけます。思考が現実を創る／全てを受けとめるという智恵を、あなたに授けようとしています。愛の成長の核心に対するハワイアンタロットの答えは明確です。それは意思です。この意思という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。意思は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で意思を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1103, 110309, 11030904, 'タロットが告げるのは【KANE】。創造というキーワードで、もう一度始めることを伝えています。カードが示す愛の成長の核心の答えは、創造にあります。この創造を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、創造という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、創造の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。');
