INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1106, '二つの顔徹底鑑定　誠実な気持ちと省みること', '運命を読み解く', '求める相手の本音の愛、そして選択の先…　ハワイアンタロットが許されぬ愛の全てをお伝えします。', 4, NULL, NULL, NULL, NULL, NULL, '2026-03-06 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1106, 110601, '決めた未来への道筋'),
(1106, 110602, '見つめ直すことの意味'),
(1106, 110603, '忘れられない人の誠実な気持ち'),
(1106, 110604, '選択することがもたらす関係の結末'),
(1106, 110605, '憧れの人が望む真実の想い'),
(1106, 110606, '内なる戦いにおける重要ポイント'),
