INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1108, '家族への思いの答え　分かち合う人の共に築く未来を占う', '運命鑑定', '将来の夫の将来への考え、そして家族の姿…　ハワイアンタロットが生涯のパートナーの全てをお伝えします。', 6, NULL, NULL, NULL, NULL, NULL, '2026-03-08 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1108, 110801, '学ぶべきこと'),
(1108, 110802, '決めることがもたらす結婚後の生活'),
(1108, 110803, '生涯の約束の核心'),
(1108, 110804, '生活の展望の可能性'),
(1108, 110805, '真意の深層'),
(1108, 110806, '結婚の意味における支え合うこと');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1108, 110801, 11080101, '【PO/AO】のカードが示されました。「深層心理を探る」というメッセージが込められています。あなたが知りたい学ぶべきことの核心は、無意識/意識にあります。無意識/意識というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。無意識/意識を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1108, 110801, 11080102, '出たカードは【TI】。感情、肉体、精神の浄化をすることを示す重要なカードです。学ぶべきことにおいて、浄化が極めて重要な役割を果たしています。この浄化のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。浄化という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1108, 110801, 11080103, 'タロットが告げるのは【PELE】。隆起というキーワードで、情熱を注ぐことを伝えています。カードが明かす学ぶべきことの真実において、隆起は中心的な役割を果たしています。隆起を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。隆起のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、隆起という贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1108, 110801, 11080104, 'ハワイアンタロットの【I’O】。ミステリーのエネルギーを持ち、全ては必然で起こっていることを説いています。学ぶべきことを考える上で、ミステリーは欠かせない要素となっています。ミステリーの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、ミステリーの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、ミステリーのメッセージを受け取りましょう。道は必ず開けます。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1108, 110802, 11080201, '【LONO/KAMAPUA’A】のカードが語りかけます。やり切ることで収穫に繋がるという智恵を、あなたに授けようとしています。決めることがもたらす結婚後の生活において、収穫/豊穣が極めて重要な役割を果たしています。この収穫/豊穣のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。収穫/豊穣という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1108, 110802, 11080202, '【AWA】のカードが出ました。このカードは「無条件と見返りの違いを意識する」を意味します。決めることがもたらす結婚後の生活において最も大切なのは、供物・生贄という概念を正しく認識することです。供物・生贄は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて供物・生贄と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、供物・生贄と共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1108, 110802, 11080203, '【KANALOA】のカードが語りかけます。基盤づくりをするという智恵を、あなたに授けようとしています。決めることがもたらす結婚後の生活に対するハワイアンタロットの答えは明確です。それは基盤です。この基盤という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。基盤は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で基盤を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1108, 110802, 11080204, '【TAPA】のカードが語りかけます。パートナーシップ、距離感を考えるという智恵を、あなたに授けようとしています。タロットが告げる決めることがもたらす結婚後の生活に関する真実は、結合に集約されています。結合という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。結合を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1108, 110803, 11080301, '【LA’AMAOMAO】のカードが語りかけます。失敗は成功の学びという智恵を、あなたに授けようとしています。タロットが告げる生涯の約束の核心に関する真実は、償いに集約されています。償いという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。償いを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1108, 110803, 11080302, '【IWI】のカードが出ました。このカードは「自分も他人も大切にする」を意味します。恋愛における生涯の約束の核心を占う上で、尊敬以上に重要なものはありません。尊敬は、二人の関係を支える基盤であり、愛を育む土壌です。この尊敬を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、尊敬があれば必ず乗り越えられます。愛を信じ、尊敬と共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1108, 110803, 11080303, '出たカードは【ANUENUE】。隠れた部分に光をあてることを示す重要なカードです。あなたの生涯の約束の核心には、変容が深く関わっています。この変容というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。変容のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、変容と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1108, 110803, 11080304, '【ANO’ANO】のカードが出ました。このカードは「思考が現実を創る／全てを受けとめる」を意味します。カードが明かす生涯の約束の核心の真実において、意思は中心的な役割を果たしています。意思を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。意思のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、意思という贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1108, 110804, 11080401, '【LAKA】のカードが語りかけます。直感を大切にするという智恵を、あなたに授けようとしています。生活の展望の可能性を考える上で、インスピレーションは欠かせない要素となっています。インスピレーションの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、インスピレーションの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、インスピレーションのメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1108, 110804, 11080402, 'カードに現れたのは【MO’O】。テーマは「不安が成功を妨げる」です。タロットが告げる生活の展望の可能性に関する真実は、罠に集約されています。罠という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。罠を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1108, 110804, 11080403, '【HULU】のカードが示されました。「無限の可能性」というメッセージが込められています。あなたが知りたい生活の展望の可能性の核心は、イリュージョンにあります。イリュージョンというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。イリュージョンを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1108, 110804, 11080404, '【PUA】のカードが示されました。「目的を定め前進あるのみ」というメッセージが込められています。あなたの生活の展望の可能性には、前進が深く関わっています。この前進というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。前進のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、前進と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1108, 110805, 11080501, 'タロットが告げるのは【ALOHA】。愛というキーワードで、真の愛を学ぶことを伝えています。あなたの真意の深層には、愛が深く関わっています。この愛というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。愛のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、愛と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1108, 110805, 11080502, 'タロットが告げるのは【PAPA&WAKEA】。苦闘・全体というキーワードで、葛藤の中から新しいものを生み出すことを伝えています。真意の深層を考える上で、苦闘・全体は欠かせない要素となっています。苦闘・全体の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、苦闘・全体の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、苦闘・全体のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1108, 110805, 11080503, '【WA’A】のカードが出ました。このカードは「大きな変化が訪れる」を意味します。あなたが知りたい真意の深層の核心は、移動にあります。移動というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。移動を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1108, 110805, 11080504, 'タロットが告げるのは【PAHU】。タイミング・儀式というキーワードで、ターニングポイントことを伝えています。真意の深層に秘められた真実は、タイミング・儀式によって明らかになります。愛に関わるあらゆる疑問や不安は、タイミング・儀式という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、タイミング・儀式を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。タイミング・儀式さえ忘れなければ、二人の未来は明るく輝くでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1108, 110806, 11080601, '【PUEO】のカードが示されました。「自然の流れに身をまかせる」というメッセージが込められています。あなたの結婚の意味における支え合うことには、導きが深く関わっています。この導きというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。導きのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、導きと共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1108, 110806, 11080602, '【HAUMEA】のカードが現れました。滋養を象徴し、必要なものは与えられることを教えてくれます。結婚の意味における支え合うことにおいて、滋養が極めて重要な役割を果たしています。この滋養のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。滋養という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1108, 110806, 11080603, '出たカードは【MANA】。満ちているエネルギーをどう使うかことを示す重要なカードです。カードが明かす結婚の意味における支え合うことの真実において、力を与えることは中心的な役割を果たしています。力を与えることを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。力を与えることのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、力を与えることという贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1108, 110806, 11080604, '【POLI’AHU】のカードが示されました。「不動の静けさからくる強さ」というメッセージが込められています。恋愛における結婚の意味における支え合うことを占う上で、静けさの愛撫以上に重要なものはありません。静けさの愛撫は、二人の関係を支える基盤であり、愛を育む土壌です。この静けさの愛撫を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、静けさの愛撫があれば必ず乗り越えられます。愛を信じ、静けさの愛撫と共に歩んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。');
