INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1121, '実行すべきことで変わる復縁後の未来　関係の再開の真相', '復縁の可能性を知る', '復縁の展望を見据えて。後悔から読み解く愛の再燃を、ハワイアンタロットが詳しくお伝えします。', 9, NULL, NULL, NULL, NULL, NULL, '2026-03-21 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1121, 112101, '縁を繋ぎたい人との復縁の展望'),
(1121, 112102, '元パートナーの視点から見た未練'),
(1121, 112103, '失った人にとっての理解すること'),
(1121, 112104, '忘れられない人が感じていること'),
(1121, 112105, 'かつての相手との関係の蘇生'),
(1121, 112106, 'もう一度の愛における見つめ直すこと');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1121, 112101, 11210101, '【LONO/KAMAPUA’A】のカードです。LONO/KAMAPUA’Aが示すのは収穫/豊穣であり、やり切ることで収穫に繋がることの重要性です。タロットが告げる縁を繋ぎたい人との復縁の展望に関する真実は、収穫/豊穣に集約されています。収穫/豊穣という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。収穫/豊穣を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112101, 11210102, 'ハワイアンタロットの【OLE】。物語のエネルギーを持ち、言葉を大切にすることを説いています。恋愛における縁を繋ぎたい人との復縁の展望を占う上で、物語以上に重要なものはありません。物語は、二人の関係を支える基盤であり、愛を育む土壌です。この物語を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、物語があれば必ず乗り越えられます。愛を信じ、物語と共に歩んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112101, 11210103, '【HUNA】のカードが現れました。秘密を象徴し、可能性を禁じないことを教えてくれます。縁を繋ぎたい人との復縁の展望を考える上で、秘密は欠かせない要素となっています。秘密の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、秘密の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、秘密のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112101, 11210104, '【TI】のカードが語りかけます。感情、肉体、精神の浄化をするという智恵を、あなたに授けようとしています。縁を繋ぎたい人との復縁の展望において最も大切なのは、浄化という概念を正しく認識することです。浄化は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて浄化と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、浄化と共に成長していきましょう。未来は明るく輝いています。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1121, 112102, 11210201, '【HO’OPONOPONO】のカードが示されました。「コミュニケーションを大切にする」というメッセージが込められています。元パートナーの視点から見た未練を考える上で、コミュニケーションは欠かせない要素となっています。コミュニケーションの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、コミュニケーションの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、コミュニケーションのメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1121, 112102, 11210202, 'ハワイアンタロットの【AWA】。供物・生贄のエネルギーを持ち、無条件と見返りの違いを意識することを説いています。元パートナーの視点から見た未練に対するハワイアンタロットの答えは明確です。それは供物・生贄です。この供物・生贄という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。供物・生贄は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で供物・生贄を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1121, 112102, 11210203, 'カードに現れたのは【PELE】。テーマは「情熱を注ぐ」です。カードが示す元パートナーの視点から見た未練の答えは、隆起にあります。この隆起を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、隆起という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、隆起の導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1121, 112102, 11210204, '【LA’AMAOMAO】のカードが示されました。「失敗は成功の学び」というメッセージが込められています。カードが示す元パートナーの視点から見た未練の答えは、償いにあります。この償いを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、償いという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、償いの導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112103, 11210301, '【POLI’AHU】のカードが示されました。「不動の静けさからくる強さ」というメッセージが込められています。あなたの失った人にとっての理解することには、静けさの愛撫が深く関わっています。この静けさの愛撫というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。静けさの愛撫のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、静けさの愛撫と共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112103, 11210302, 'ハワイアンタロットの【LEI】。大切のエネルギーを持ち、日々の暮らしを大切にことを説いています。カードが明かす失った人にとっての理解することの真実において、大切は中心的な役割を果たしています。大切を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。大切のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、大切という贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112103, 11210303, '出たカードは【PU’OLO】。不必要なものを手放すことを示す重要なカードです。カードが示す失った人にとっての理解することの答えは、解放にあります。この解放を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、解放という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、解放の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1121, 112103, 11210304, 'タロットが告げるのは【PUA】。前進というキーワードで、目的を定め前進あるのみことを伝えています。失った人にとっての理解することに関して、カードは前進という重要なメッセージを伝えています。あなたが今必要としているのは、この前進を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。前進の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1121, 112104, 11210401, 'ハワイアンタロットの【WA’A】。移動のエネルギーを持ち、大きな変化が訪れることを説いています。忘れられない人が感じていることに関して、カードは移動という重要なメッセージを伝えています。あなたが今必要としているのは、この移動を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。移動の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112104, 11210402, '【LAMAKU】のカードが現れました。勝利を象徴し、意思の力で喜びを手にすることを教えてくれます。忘れられない人が感じていることを考える上で、勝利は欠かせない要素となっています。勝利の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、勝利の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、勝利のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112104, 11210403, '【MANO】のカードが示されました。「リーダーシップを取る」というメッセージが込められています。忘れられない人が感じていることにおいて、リーダーシップが極めて重要な役割を果たしています。このリーダーシップのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。リーダーシップという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1121, 112104, 11210404, 'カードに現れたのは【IWI】。テーマは「自分も他人も大切にする」です。忘れられない人が感じていることに対するハワイアンタロットの答えは明確です。それは尊敬です。この尊敬という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。尊敬は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で尊敬を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1121, 112105, 11210501, '出たカードは【I’O】。全ては必然で起こっていることを示す重要なカードです。かつての相手との関係の蘇生に関して、カードはミステリーという重要なメッセージを伝えています。あなたが今必要としているのは、このミステリーを深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。ミステリーの真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1121, 112105, 11210502, '【PAHU】のカードが示されました。「ターニングポイント」というメッセージが込められています。かつての相手との関係の蘇生に対するハワイアンタロットの答えは明確です。それはタイミング・儀式です。このタイミング・儀式という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。タイミング・儀式は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でタイミング・儀式を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1121, 112105, 11210503, 'タロットが告げるのは【MAUI】。トリックスターというキーワードで、失敗を恐れずチャレンジすることを伝えています。かつての相手との関係の蘇生において最も大切なのは、トリックスターという概念を正しく認識することです。トリックスターは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてトリックスターと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、トリックスターと共に成長していきましょう。未来は明るく輝いています。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1121, 112105, 11210504, 'ハワイアンタロットの【KA WAI A KA PILILIKO】。反映のエネルギーを持ち、ありのままを受け入れることを説いています。かつての相手との関係の蘇生に秘められた真実は、反映によって明らかになります。愛に関わるあらゆる疑問や不安は、反映という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、反映を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。反映さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1121, 112106, 11210601, 'カードに現れたのは【HAUMEA】。テーマは「必要なものは与えられる」です。もう一度の愛における見つめ直すことに秘められた真実は、滋養によって明らかになります。愛に関わるあらゆる疑問や不安は、滋養という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、滋養を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。滋養さえ忘れなければ、二人の未来は明るく輝くでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1121, 112106, 11210602, 'ハワイアンタロットの【MAMALAHOE】。同情のエネルギーを持ち、自分の気持ちを最優先することを説いています。タロットが告げるもう一度の愛における見つめ直すことに関する真実は、同情に集約されています。同情という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。同情を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1121, 112106, 11210603, '【ALOHA】のカードが現れました。愛を象徴し、真の愛を学ぶことを教えてくれます。もう一度の愛における見つめ直すことに秘められた真実は、愛によって明らかになります。愛に関わるあらゆる疑問や不安は、愛という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、愛を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。愛さえ忘れなければ、二人の未来は明るく輝くでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1121, 112106, 11210604, '【HINA】のカードが語りかけます。自分自身を成長させるという智恵を、あなたに授けようとしています。あなたのもう一度の愛における見つめ直すことには、養いが深く関わっています。この養いというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。養いのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、養いと共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。');
