INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1122, '恋の扉を開くあなたとの絆　運命の相手を占う縁結び', '恋愛オーラ全解明', '魅力度が気になる…　運命の相手はどうなる？　ハワイアンタロットが縁を大切にするあなたの真実を明らかにします。', 10, NULL, NULL, NULL, NULL, NULL, '2026-03-22 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1122, 112201, '出会いの波動が示すもの'),
(1122, 112202, '恋愛傾向における縁結び'),
(1122, 112203, '恋愛オーラの深層'),
(1122, 112204, '出会いを願うあなたが感じていること'),
(1122, 112205, '恋愛運の核心'),
(1122, 112206, '運命の出会いの核心'),
(1122, 112207, '恋に憧れる自分の視点から見た今の恋愛運'),
