INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1129, '内なる喜びを読み解く　現在の環境への扉', '現在のレベル完全予測', '人生の選択の真相を知りたい…　本来の姿の深い喜びと今この瞬間を、ハワイアンタロットが解き明かします。', 7, NULL, NULL, NULL, NULL, NULL, '2026-03-29 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1129, 112901, '深い喜びと今この瞬間の関係'),
(1129, 112902, '自己実現の行方'),
(1129, 112903, '存在意義の真相'),
(1129, 112904, '人生の軌跡の行方'),
(1129, 112905, '本来の存在が大切にすること'),
(1129, 112906, '今のステージへの精神の安寧'),
(1129, 112907, '充実感と今の境遇の関係'),
(1129, 112908, '運命の道における重要ポイント'),
