INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1143, '素直な感情の全体像　築く未来完全解読', '真の気持ちの真相', '関係の成熟の真相を知りたい…　彼女の素直な感情と二人の行方を、ハワイアンタロットが解き明かします。', 1, NULL, NULL, NULL, NULL, NULL, '2026-04-12 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1143, 114301, '絆の強化を左右する要因'),
(1143, 114302, '二人が求める二人の未来'),
(1143, 114303, '大事にすることの意味'),
(1143, 114304, 'ソウルメイトの続く物語に対する想い'),
(1143, 114305, '迎える明日における二人の役割'),
(1143, 114306, '将来の関係を実現する大切にすること');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1143, 114301, 11430101, 'タロットが告げるのは【PUA】。前進というキーワードで、目的を定め前進あるのみことを伝えています。絆の強化を左右する要因に対するハワイアンタロットの答えは明確です。それは前進です。この前進という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。前進は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で前進を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1143, 114301, 11430102, '【PUHI】のカードです。PUHIが示すのは崩壊であり、新しい創造のための破壊ことの重要性です。あなたの絆の強化を左右する要因には、崩壊が深く関わっています。この崩壊というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。崩壊のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、崩壊と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1143, 114301, 11430103, '【KANALOA】のカードが語りかけます。基盤づくりをするという智恵を、あなたに授けようとしています。絆の強化を左右する要因に対するハワイアンタロットの答えは明確です。それは基盤です。この基盤という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。基盤は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で基盤を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1143, 114301, 11430104, 'カードに現れたのは【OLE】。テーマは「言葉を大切にする」です。絆の強化を左右する要因に関して、カードは物語という重要なメッセージを伝えています。あなたが今必要としているのは、この物語を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。物語の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1143, 114302, 11430201, 'ハワイアンタロットの【LEHUA】。イニシエーションのエネルギーを持ち、安心して前進することを説いています。カードが示す二人が求める二人の未来の答えは、イニシエーションにあります。このイニシエーションを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、イニシエーションという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、イニシエーションの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1143, 114302, 11430202, '【MO’O】のカードが語りかけます。不安が成功を妨げるという智恵を、あなたに授けようとしています。恋愛における二人が求める二人の未来を占う上で、罠以上に重要なものはありません。罠は、二人の関係を支える基盤であり、愛を育む土壌です。この罠を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、罠があれば必ず乗り越えられます。愛を信じ、罠と共に歩んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1143, 114302, 11430203, '【KU/HINA】のカードです。KU/HINAが示すのは戦い/癒しであり、活動する/休息することの重要性です。タロットが告げる二人が求める二人の未来に関する真実は、戦い/癒しに集約されています。戦い/癒しという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。戦い/癒しを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1143, 114302, 11430204, '【POLI’AHU】のカードです。POLI’AHUが示すのは静けさの愛撫であり、不動の静けさからくる強さことの重要性です。二人が求める二人の未来において、静けさの愛撫が極めて重要な役割を果たしています。この静けさの愛撫のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。静けさの愛撫という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1143, 114303, 11430301, '【POHAKU】のカードが示されました。「人間関係を見直す」というメッセージが込められています。あなたが知りたい大事にすることの意味の核心は、関係にあります。関係というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。関係を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1143, 114303, 11430302, 'タロットが告げるのは【HI’IAKA】。献身というキーワードで、望む結果をイメージすることを伝えています。大事にすることの意味を考える上で、献身は欠かせない要素となっています。献身の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、献身の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、献身のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1143, 114303, 11430303, '出たカードは【PAPA&WAKEA】。葛藤の中から新しいものを生み出すことを示す重要なカードです。恋愛における大事にすることの意味を占う上で、苦闘・全体以上に重要なものはありません。苦闘・全体は、二人の関係を支える基盤であり、愛を育む土壌です。この苦闘・全体を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、苦闘・全体があれば必ず乗り越えられます。愛を信じ、苦闘・全体と共に歩んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1143, 114303, 11430304, '【MAMALAHOE】のカードが示されました。「自分の気持ちを最優先する」というメッセージが込められています。恋愛における大事にすることの意味を占う上で、同情以上に重要なものはありません。同情は、二人の関係を支える基盤であり、愛を育む土壌です。この同情を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、同情があれば必ず乗り越えられます。愛を信じ、同情と共に歩んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1143, 114304, 11430401, '【LONO/KAMAPUA’A】のカードが示されました。「やり切ることで収穫に繋がる」というメッセージが込められています。カードが明かすソウルメイトの続く物語に対する想いの真実において、収穫/豊穣は中心的な役割を果たしています。収穫/豊穣を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。収穫/豊穣のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、収穫/豊穣という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1143, 114304, 11430402, 'タロットが告げるのは【HUNA】。秘密というキーワードで、可能性を禁じないことを伝えています。ソウルメイトの続く物語に対する想いを考える上で、秘密は欠かせない要素となっています。秘密の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、秘密の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、秘密のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1143, 114304, 11430403, '【HAUMEA】のカードが現れました。滋養を象徴し、必要なものは与えられることを教えてくれます。ソウルメイトの続く物語に対する想いに秘められた真実は、滋養によって明らかになります。愛に関わるあらゆる疑問や不安は、滋養という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、滋養を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。滋養さえ忘れなければ、二人の未来は明るく輝くでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1143, 114304, 11430404, '【KANE】のカードが語りかけます。もう一度始めるという智恵を、あなたに授けようとしています。タロットが告げるソウルメイトの続く物語に対する想いに関する真実は、創造に集約されています。創造という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。創造を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1143, 114305, 11430501, '【MAUI】が選ばれました。「失敗を恐れずチャレンジする」がこのカードの核心的メッセージです。タロットが告げる迎える明日における二人の役割に関する真実は、トリックスターに集約されています。トリックスターという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。トリックスターを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1143, 114305, 11430502, '出たカードは【KA WAI A KA PILILIKO】。ありのままを受け入れることを示す重要なカードです。恋愛における迎える明日における二人の役割を占う上で、反映以上に重要なものはありません。反映は、二人の関係を支える基盤であり、愛を育む土壌です。この反映を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、反映があれば必ず乗り越えられます。愛を信じ、反映と共に歩んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1143, 114305, 11430503, '【PELE】のカードです。PELEが示すのは隆起であり、情熱を注ぐことの重要性です。タロットが告げる迎える明日における二人の役割に関する真実は、隆起に集約されています。隆起という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。隆起を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1143, 114305, 11430504, 'カードに現れたのは【HO’OPONOPONO】。テーマは「コミュニケーションを大切にする」です。あなたの迎える明日における二人の役割には、コミュニケーションが深く関わっています。このコミュニケーションというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。コミュニケーションのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、コミュニケーションと共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1143, 114306, 11430601, '【IWI】のカードが出ました。このカードは「自分も他人も大切にする」を意味します。タロットが告げる将来の関係を実現する大切にすることに関する真実は、尊敬に集約されています。尊敬という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。尊敬を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1143, 114306, 11430602, '【TAPA】のカードです。TAPAが示すのは結合であり、パートナーシップ、距離感を考えることの重要性です。あなたの将来の関係を実現する大切にすることには、結合が深く関わっています。この結合というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。結合のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、結合と共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1143, 114306, 11430603, 'カードに現れたのは【ALOHA】。テーマは「真の愛を学ぶ」です。将来の関係を実現する大切にすることに秘められた真実は、愛によって明らかになります。愛に関わるあらゆる疑問や不安は、愛という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、愛を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。愛さえ忘れなければ、二人の未来は明るく輝くでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1143, 114306, 11430604, '【ANO’ANO】のカードが語りかけます。思考が現実を創る／全てを受けとめるという智恵を、あなたに授けようとしています。タロットが告げる将来の関係を実現する大切にすることに関する真実は、意思に集約されています。意思という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。意思を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。');
