INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1145, '今後の見込みへの道　心情の解明完全ガイド', '運命の扉を開く', '誠実な感情と先の見通しについて。当の本人に関する真意の把握を、ハワイアンタロットが徹底解明。', 3, NULL, NULL, NULL, NULL, NULL, '2026-04-14 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1145, 114501, '先行きの予測を導く薦められる方法'),
(1145, 114502, '想いの真実における重要ポイント'),
(1145, 114503, '心情の実像を左右する要因'),
(1145, 114504, '当の本人の今後の流れに対する想い'),
(1145, 114505, '本当の感情が教えてくれること'),
(1145, 114506, '感情の真髄の核心');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1145, 114501, 11450101, '【I’O】のカードが出ました。このカードは「全ては必然で起こっている」を意味します。先行きの予測を導く薦められる方法に関して、カードはミステリーという重要なメッセージを伝えています。あなたが今必要としているのは、このミステリーを深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。ミステリーの真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1145, 114501, 11450102, '【PUHI】のカードが示されました。「新しい創造のための破壊」というメッセージが込められています。先行きの予測を導く薦められる方法に対するハワイアンタロットの答えは明確です。それは崩壊です。この崩壊という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。崩壊は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で崩壊を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1145, 114501, 11450103, '出たカードは【MANO】。リーダーシップを取ることを示す重要なカードです。タロットが告げる先行きの予測を導く薦められる方法に関する真実は、リーダーシップに集約されています。リーダーシップという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。リーダーシップを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1145, 114501, 11450104, '【LONO/KAMAPUA’A】のカードが出ました。このカードは「やり切ることで収穫に繋がる」を意味します。先行きの予測を導く薦められる方法を考える上で、収穫/豊穣は欠かせない要素となっています。収穫/豊穣の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、収穫/豊穣の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、収穫/豊穣のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1145, 114502, 11450201, 'ハワイアンタロットの【MAUI】。トリックスターのエネルギーを持ち、失敗を恐れずチャレンジすることを説いています。タロットが告げる想いの真実における重要ポイントに関する真実は、トリックスターに集約されています。トリックスターという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。トリックスターを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1145, 114502, 11450202, '【HO’OPONOPONO】のカードが出ました。このカードは「コミュニケーションを大切にする」を意味します。カードが示す想いの真実における重要ポイントの答えは、コミュニケーションにあります。このコミュニケーションを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、コミュニケーションという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、コミュニケーションの導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1145, 114502, 11450203, 'カードに現れたのは【MO’O】。テーマは「不安が成功を妨げる」です。カードが示す想いの真実における重要ポイントの答えは、罠にあります。この罠を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、罠という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、罠の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1145, 114502, 11450204, 'タロットが告げるのは【PU’OLO】。解放というキーワードで、不必要なものを手放すことを伝えています。想いの真実における重要ポイントに関して、カードは解放という重要なメッセージを伝えています。あなたが今必要としているのは、この解放を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。解放の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1145, 114503, 11450301, 'ハワイアンタロットの【ANO’ANO】。意思のエネルギーを持ち、思考が現実を創る／全てを受けとめることを説いています。心情の実像を左右する要因を考える上で、意思は欠かせない要素となっています。意思の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、意思の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、意思のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1145, 114503, 11450302, '【POLI’AHU】のカードが出ました。このカードは「不動の静けさからくる強さ」を意味します。カードが示す心情の実像を左右する要因の答えは、静けさの愛撫にあります。この静けさの愛撫を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、静けさの愛撫という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、静けさの愛撫の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1145, 114503, 11450303, 'ハワイアンタロットの【LEHUA】。イニシエーションのエネルギーを持ち、安心して前進することを説いています。心情の実像を左右する要因において最も大切なのは、イニシエーションという概念を正しく認識することです。イニシエーションは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてイニシエーションと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、イニシエーションと共に成長していきましょう。未来は明るく輝いています。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1145, 114503, 11450304, '【LAKA】のカードが語りかけます。直感を大切にするという智恵を、あなたに授けようとしています。心情の実像を左右する要因において、インスピレーションが極めて重要な役割を果たしています。このインスピレーションのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。インスピレーションという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1145, 114504, 11450401, '【ALOHA】が選ばれました。「真の愛を学ぶ」がこのカードの核心的メッセージです。あなたの当の本人の今後の流れに対する想いには、愛が深く関わっています。この愛というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。愛のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、愛と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1145, 114504, 11450402, 'ハワイアンタロットの【PUA】。前進のエネルギーを持ち、目的を定め前進あるのみことを説いています。カードが示す当の本人の今後の流れに対する想いの答えは、前進にあります。この前進を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、前進という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、前進の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1145, 114504, 11450403, '【HI’IAKA】のカードが語りかけます。望む結果をイメージするという智恵を、あなたに授けようとしています。当の本人の今後の流れに対する想いにおいて、献身が極めて重要な役割を果たしています。この献身のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。献身という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1145, 114504, 11450404, '【NAUPAKA】が選ばれました。「切り離す」がこのカードの核心的メッセージです。タロットが告げる当の本人の今後の流れに対する想いに関する真実は、別離に集約されています。別離という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。別離を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1145, 114505, 11450501, 'ハワイアンタロットの【WA’A】。移動のエネルギーを持ち、大きな変化が訪れることを説いています。タロットが告げる本当の感情が教えてくれることに関する真実は、移動に集約されています。移動という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。移動を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1145, 114505, 11450502, '【HULU】が選ばれました。「無限の可能性」がこのカードの核心的メッセージです。カードが明かす本当の感情が教えてくれることの真実において、イリュージョンは中心的な役割を果たしています。イリュージョンを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。イリュージョンのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、イリュージョンという贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1145, 114505, 11450503, '【LA’AMAOMAO】が選ばれました。「失敗は成功の学び」がこのカードの核心的メッセージです。カードが示す本当の感情が教えてくれることの答えは、償いにあります。この償いを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、償いという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、償いの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1145, 114505, 11450504, '【POHAKU】のカードが現れました。関係を象徴し、人間関係を見直すことを教えてくれます。カードが示す本当の感情が教えてくれることの答えは、関係にあります。この関係を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、関係という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、関係の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1145, 114506, 11450601, '【MENEHUNE】が選ばれました。「コツコツと積み重ねる」がこのカードの核心的メッセージです。感情の真髄の核心において最も大切なのは、持続という概念を正しく認識することです。持続は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて持続と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、持続と共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1145, 114506, 11450602, '【TAPA】が選ばれました。「パートナーシップ、距離感を考える」がこのカードの核心的メッセージです。タロットが告げる感情の真髄の核心に関する真実は、結合に集約されています。結合という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。結合を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1145, 114506, 11450603, '【PUEO】のカードです。PUEOが示すのは導きであり、自然の流れに身をまかせることの重要性です。カードが示す感情の真髄の核心の答えは、導きにあります。この導きを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、導きという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、導きの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1145, 114506, 11450604, '【MAMALAHOE】のカードが語りかけます。自分の気持ちを最優先するという智恵を、あなたに授けようとしています。感情の真髄の核心に対するハワイアンタロットの答えは明確です。それは同情です。この同情という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。同情は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で同情を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。');
