INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1146, 114601, '愛する人にとっての選択すること'),
(1146, 114602, '揺れる心としての選ぶべきこと'),
(1146, 114603, '究極の選択の行方'),
