INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1149, '今のステージの行方　価値観詳細鑑定', '心の平安の真相', '内なる自己の真の幸せ、そして今の段階…　ハワイアンタロットが生き方の全てをお伝えします。', 7, NULL, NULL, NULL, NULL, NULL, '2026-04-18 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1149, 114901, '真の幸せが教えてくれること'),
(1149, 114902, '現状における自分の役割'),
(1149, 114903, '素の自分が大切にすること'),
(1149, 114904, '現在の位置の展望'),
(1149, 114905, '現在の位置の展望'),
(1149, 114906, '真実の自己が望む心の充足');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1149, 114901, 11490101, '【MANO】のカードです。MANOが示すのはリーダーシップであり、リーダーシップを取ることの重要性です。カードが示す真の幸せが教えてくれることの答えは、リーダーシップにあります。このリーダーシップを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、リーダーシップという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、リーダーシップの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1149, 114901, 11490102, '【LEHUA】のカードが語りかけます。安心して前進するという智恵を、あなたに授けようとしています。キャリアや人生設計における真の幸せが教えてくれることを考える際、イニシエーションは最重要ファクターです。成功者に共通するのは、このイニシエーションを深く理解し、実践していることです。イニシエーションという軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、イニシエーションを磨き続けてください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1149, 114901, 11490103, 'カードに現れたのは【MO’O】。テーマは「不安が成功を妨げる」です。真の幸せが教えてくれることにおいて最も大切なのは、罠という概念を正しく認識することです。罠は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて罠と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、罠と共に成長していきましょう。未来は明るく輝いています。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1149, 114901, 11490104, '【LAMAKU】のカードが出ました。このカードは「意思の力で喜びを手にする」を意味します。タロットが告げる真の幸せが教えてくれることに関する真実は、勝利に集約されています。勝利という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。勝利を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1149, 114902, 11490201, '【ANO’ANO】のカードが語りかけます。思考が現実を創る／全てを受けとめるという智恵を、あなたに授けようとしています。現状における自分の役割に対するハワイアンタロットの答えは明確です。それは意思です。この意思という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。意思は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で意思を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1149, 114902, 11490202, '【TAPA】のカードが現れました。結合を象徴し、パートナーシップ、距離感を考えることを教えてくれます。現状における自分の役割に対するハワイアンタロットの答えは明確です。それは結合です。この結合という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。結合は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で結合を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1149, 114902, 11490203, '【PUA】のカードが示されました。「目的を定め前進あるのみ」というメッセージが込められています。カードが示す現状における自分の役割の答えは、前進にあります。この前進を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、前進という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、前進の導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1149, 114902, 11490204, '【NALU】のカードです。NALUが示すのは黙想であり、感情を観察することの重要性です。あなたの現状における自分の役割には、黙想が深く関わっています。この黙想というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。黙想のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、黙想と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1149, 114903, 11490301, 'ハワイアンタロットの【PO/AO】。無意識/意識のエネルギーを持ち、深層心理を探ることを説いています。カードが明かす素の自分が大切にすることの真実において、無意識/意識は中心的な役割を果たしています。無意識/意識を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。無意識/意識のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、無意識/意識という贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1149, 114903, 11490302, '【POHAKU】が選ばれました。「人間関係を見直す」がこのカードの核心的メッセージです。素の自分が大切にすることに対するハワイアンタロットの答えは明確です。それは関係です。この関係という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。関係は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で関係を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1149, 114903, 11490303, 'カードに現れたのは【LAKA】。テーマは「直感を大切にする」です。素の自分が大切にすることという人生の重要なテーマにおいて、インスピレーションは羅針盤となります。人生の岐路に立ったとき、インスピレーションを指針とすることで、正しい選択ができるでしょう。インスピレーションは、あなたの使命や天命とも深く関わっています。このインスピレーションを追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、インスピレーションに従って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1149, 114903, 11490304, '【PAHU】のカードが出ました。このカードは「ターニングポイント」を意味します。素の自分が大切にすることにおいて、タイミング・儀式が極めて重要な役割を果たしています。このタイミング・儀式のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。タイミング・儀式という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1149, 114904, 11490401, '【PAPA&WAKEA】のカードが語りかけます。葛藤の中から新しいものを生み出すという智恵を、あなたに授けようとしています。キャリアや人生設計における現在の位置の展望を考える際、苦闘・全体は最重要ファクターです。成功者に共通するのは、この苦闘・全体を深く理解し、実践していることです。苦闘・全体という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、苦闘・全体を磨き続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1149, 114904, 11490402, 'カードに現れたのは【PUHI】。テーマは「新しい創造のための破壊」です。現在の位置の展望において最も大切なのは、崩壊という概念を正しく認識することです。崩壊は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて崩壊と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、崩壊と共に成長していきましょう。未来は明るく輝いています。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1149, 114904, 11490403, '【ANUENUE】のカードです。ANUENUEが示すのは変容であり、隠れた部分に光をあてることの重要性です。あなたの現在の位置の展望には、変容が深く関わっています。この変容というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。変容のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、変容と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1149, 114904, 11490404, '【POLI’AHU】のカードが語りかけます。不動の静けさからくる強さという智恵を、あなたに授けようとしています。あなたの現在の位置の展望には、静けさの愛撫が深く関わっています。この静けさの愛撫というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。静けさの愛撫のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、静けさの愛撫と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1149, 114905, 11490501, 'カードに現れたのは【ALOHA】。テーマは「真の愛を学ぶ」です。キャリアや人生設計における現在の位置の展望を考える際、愛は最重要ファクターです。成功者に共通するのは、この愛を深く理解し、実践していることです。愛という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、愛を磨き続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1149, 114905, 11490502, 'ハワイアンタロットの【LONO/KAMAPUA’A】。収穫/豊穣のエネルギーを持ち、やり切ることで収穫に繋がることを説いています。あなたが知りたい現在の位置の展望の核心は、収穫/豊穣にあります。収穫/豊穣というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。収穫/豊穣を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1149, 114905, 11490503, '【LEI】のカードが出ました。このカードは「日々の暮らしを大切に」を意味します。あなたが知りたい現在の位置の展望の核心は、大切にあります。大切というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。大切を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1149, 114905, 11490504, '【HUNA】のカードが出ました。このカードは「可能性を禁じない」を意味します。カードが示す現在の位置の展望の答えは、秘密にあります。この秘密を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、秘密という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、秘密の導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1149, 114906, 11490601, '出たカードは【HULU】。無限の可能性ことを示す重要なカードです。カードが示す真実の自己が望む心の充足の答えは、イリュージョンにあります。このイリュージョンを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、イリュージョンという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、イリュージョンの導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1149, 114906, 11490602, 'ハワイアンタロットの【PU’OLO】。解放のエネルギーを持ち、不必要なものを手放すことを説いています。カードが示す真実の自己が望む心の充足の答えは、解放にあります。この解放を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、解放という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、解放の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1149, 114906, 11490603, '【IWI】のカードが出ました。このカードは「自分も他人も大切にする」を意味します。真実の自己が望む心の充足を考える上で、尊敬は欠かせない要素となっています。尊敬の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、尊敬の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、尊敬のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1149, 114906, 11490604, 'ハワイアンタロットの【TI】。浄化のエネルギーを持ち、感情、肉体、精神の浄化をすることを説いています。タロットが告げる真実の自己が望む心の充足に関する真実は、浄化に集約されています。浄化という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。浄化を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。');
