INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1150, '職場環境完全鑑定　ワーカーとの仕事の見通し', '仕事の完成の答え', '仕事の発展を深く知る。ビジネスマンの仕事哲学、そして職業的到達点を、ハワイアンタロットが詳しく占います。', 8, NULL, NULL, NULL, NULL, NULL, '2026-04-19 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1150, 115001, '仕事への思いから見える真実'),
(1150, 115002, '実務者との仕事の先行き'),
(1150, 115003, '仕事への信念を深く知る'),
(1150, 115004, '実践すべきこと'),
(1150, 115005, '職業人としての自分との職業的展望');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1150, 115001, 11500101, '【LONO/KAMAPUA’A】のカードが語りかけます。やり切ることで収穫に繋がるという智恵を、あなたに授けようとしています。仕事への思いから見える真実に対するハワイアンタロットの答えは明確です。それは収穫/豊穣です。この収穫/豊穣という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。収穫/豊穣は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で収穫/豊穣を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1150, 115001, 11500102, '【HI’IAKA】のカードが現れました。献身を象徴し、望む結果をイメージすることを教えてくれます。タロットが告げる仕事への思いから見える真実に関する真実は、献身に集約されています。献身という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。献身を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1150, 115001, 11500103, '出たカードは【HINA】。自分自身を成長させることを示す重要なカードです。仕事への思いから見える真実に対するハワイアンタロットの答えは明確です。それは養いです。この養いという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。養いは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で養いを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1150, 115001, 11500104, '【LEHUA】のカードが現れました。イニシエーションを象徴し、安心して前進することを教えてくれます。仕事への思いから見える真実において最も大切なのは、イニシエーションという概念を正しく認識することです。イニシエーションは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてイニシエーションと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、イニシエーションと共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1150, 115002, 11500201, '【LAKA】のカードです。LAKAが示すのはインスピレーションであり、直感を大切にすることの重要性です。カードが明かす実務者との仕事の先行きの真実において、インスピレーションは中心的な役割を果たしています。インスピレーションを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。インスピレーションのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、インスピレーションという贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1150, 115002, 11500202, '【POHAKU】のカードが現れました。関係を象徴し、人間関係を見直すことを教えてくれます。実務者との仕事の先行きという人生の重要なテーマにおいて、関係は羅針盤となります。人生の岐路に立ったとき、関係を指針とすることで、正しい選択ができるでしょう。関係は、あなたの使命や天命とも深く関わっています。この関係を追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、関係に従って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1150, 115002, 11500203, '【OLE】のカードです。OLEが示すのは物語であり、言葉を大切にすることの重要性です。タロットが告げる実務者との仕事の先行きに関する真実は、物語に集約されています。物語という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。物語を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1150, 115002, 11500204, '出たカードは【MO’O】。不安が成功を妨げることを示す重要なカードです。実務者との仕事の先行きにおいて最も大切なのは、罠という概念を正しく認識することです。罠は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて罠と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、罠と共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1150, 115003, 11500301, '【IWI】のカードが示されました。「自分も他人も大切にする」というメッセージが込められています。仕事への信念を深く知るという人生の重要なテーマにおいて、尊敬は羅針盤となります。人生の岐路に立ったとき、尊敬を指針とすることで、正しい選択ができるでしょう。尊敬は、あなたの使命や天命とも深く関わっています。この尊敬を追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、尊敬に従って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1150, 115003, 11500302, '【HULU】のカードが出ました。このカードは「無限の可能性」を意味します。カードが明かす仕事への信念を深く知るの真実において、イリュージョンは中心的な役割を果たしています。イリュージョンを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。イリュージョンのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、イリュージョンという贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1150, 115003, 11500303, '【POLI’AHU】のカードが出ました。このカードは「不動の静けさからくる強さ」を意味します。仕事への信念を深く知るにおいて、静けさの愛撫が極めて重要な役割を果たしています。この静けさの愛撫のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。静けさの愛撫という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1150, 115003, 11500304, 'カードに現れたのは【PUEO】。テーマは「自然の流れに身をまかせる」です。仕事への信念を深く知るに対するハワイアンタロットの答えは明確です。それは導きです。この導きという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。導きは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で導きを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1150, 115004, 11500401, '【TAPA】のカードが語りかけます。パートナーシップ、距離感を考えるという智恵を、あなたに授けようとしています。カードが明かす実践すべきことの真実において、結合は中心的な役割を果たしています。結合を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。結合のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、結合という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1150, 115004, 11500402, 'タロットが告げるのは【PAPA&WAKEA】。苦闘・全体というキーワードで、葛藤の中から新しいものを生み出すことを伝えています。実践すべきことという人生の重要なテーマにおいて、苦闘・全体は羅針盤となります。人生の岐路に立ったとき、苦闘・全体を指針とすることで、正しい選択ができるでしょう。苦闘・全体は、あなたの使命や天命とも深く関わっています。この苦闘・全体を追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、苦闘・全体に従って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1150, 115004, 11500403, '【ANO’ANO】のカードが出ました。このカードは「思考が現実を創る／全てを受けとめる」を意味します。実践すべきことにおいて、意思が極めて重要な役割を果たしています。この意思のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。意思という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1150, 115004, 11500404, '【PELE】のカードが語りかけます。情熱を注ぐという智恵を、あなたに授けようとしています。カードが明かす実践すべきことの真実において、隆起は中心的な役割を果たしています。隆起を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。隆起のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、隆起という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1150, 115005, 11500501, 'カードに現れたのは【KU/HINA】。テーマは「活動する/休息する」です。あなたが知りたい職業人としての自分との職業的展望の核心は、戦い/癒しにあります。戦い/癒しというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。戦い/癒しを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1150, 115005, 11500502, 'ハワイアンタロットの【KA WAI A KA PILILIKO】。反映のエネルギーを持ち、ありのままを受け入れることを説いています。職業人としての自分との職業的展望という人生の重要なテーマにおいて、反映は羅針盤となります。人生の岐路に立ったとき、反映を指針とすることで、正しい選択ができるでしょう。反映は、あなたの使命や天命とも深く関わっています。この反映を追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、反映に従って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1150, 115005, 11500503, '【ALOHA】のカードが現れました。愛を象徴し、真の愛を学ぶことを教えてくれます。職業人としての自分との職業的展望において最も大切なのは、愛という概念を正しく認識することです。愛は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて愛と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、愛と共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1150, 115005, 11500504, 'ハワイアンタロットの【ANUENUE】。変容のエネルギーを持ち、隠れた部分に光をあてることを説いています。カードが示す職業人としての自分との職業的展望の答えは、変容にあります。この変容を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、変容という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、変容の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。');
