INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1167, '心通う相手が感じる一体感　強化は', '調和解明', '運命の人の満足度、そして完璧…　ハワイアンタロットが特別な時間の全てをお伝えします。', 5, NULL, NULL, NULL, NULL, NULL, '2026-05-06 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1167, 116701, '深まりを導く育むこと'),
(1167, 116702, '向上させることで変わること'),
(1167, 116703, '高めることがもたらす充実'),
(1167, 116704, '共鳴度が示すもの'),
(1167, 116705, '結ばれた相手が大切にすること'),
(1167, 116706, '強化を導く追求すること');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1167, 116701, 11670101, '【HUNA】が選ばれました。「可能性を禁じない」がこのカードの核心的メッセージです。カードが明かす深まりを導く育むことの真実において、秘密は中心的な役割を果たしています。秘密を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。秘密のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、秘密という贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1167, 116701, 11670102, '【MAUI】のカードです。MAUIが示すのはトリックスターであり、失敗を恐れずチャレンジすることの重要性です。カードが明かす深まりを導く育むことの真実において、トリックスターは中心的な役割を果たしています。トリックスターを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。トリックスターのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、トリックスターという贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1167, 116701, 11670103, 'ハワイアンタロットの【POHAKU】。関係のエネルギーを持ち、人間関係を見直すことを説いています。カードが示す深まりを導く育むことの答えは、関係にあります。この関係を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、関係という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、関係の導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1167, 116701, 11670104, '【MENEHUNE】のカードが語りかけます。コツコツと積み重ねるという智恵を、あなたに授けようとしています。あなたの深まりを導く育むことには、持続が深く関わっています。この持続というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。持続のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、持続と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1167, 116702, 11670201, '【LAKA】のカードが現れました。インスピレーションを象徴し、直感を大切にすることを教えてくれます。あなたの向上させることで変わることには、インスピレーションが深く関わっています。このインスピレーションというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。インスピレーションのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、インスピレーションと共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1167, 116702, 11670202, '【TAPA】のカードです。TAPAが示すのは結合であり、パートナーシップ、距離感を考えることの重要性です。向上させることで変わることを考える上で、結合は欠かせない要素となっています。結合の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、結合の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、結合のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1167, 116702, 11670203, '【HINA】のカードが語りかけます。自分自身を成長させるという智恵を、あなたに授けようとしています。向上させることで変わることに関して、カードは養いという重要なメッセージを伝えています。あなたが今必要としているのは、この養いを深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。養いの真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1167, 116702, 11670204, '【LAMAKU】のカードが示されました。「意思の力で喜びを手にする」というメッセージが込められています。向上させることで変わることを考える上で、勝利は欠かせない要素となっています。勝利の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、勝利の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、勝利のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1167, 116703, 11670301, '【PUEO】のカードが語りかけます。自然の流れに身をまかせるという智恵を、あなたに授けようとしています。高めることがもたらす充実において最も大切なのは、導きという概念を正しく認識することです。導きは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて導きと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、導きと共に成長していきましょう。未来は明るく輝いています。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1167, 116703, 11670302, '【LA’AMAOMAO】のカードが語りかけます。失敗は成功の学びという智恵を、あなたに授けようとしています。カードが示す高めることがもたらす充実の答えは、償いにあります。この償いを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、償いという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、償いの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1167, 116703, 11670303, '【PU’OLO】のカードが語りかけます。不必要なものを手放すという智恵を、あなたに授けようとしています。カードが明かす高めることがもたらす充実の真実において、解放は中心的な役割を果たしています。解放を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。解放のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、解放という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1167, 116703, 11670304, '【HI’IAKA】のカードが語りかけます。望む結果をイメージするという智恵を、あなたに授けようとしています。高めることがもたらす充実を考える上で、献身は欠かせない要素となっています。献身の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、献身の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、献身のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1167, 116704, 11670401, '【MANO】が選ばれました。「リーダーシップを取る」がこのカードの核心的メッセージです。タロットが告げる共鳴度が示すものに関する真実は、リーダーシップに集約されています。リーダーシップという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。リーダーシップを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1167, 116704, 11670402, '【KA WAI A KA PILILIKO】が選ばれました。「ありのままを受け入れる」がこのカードの核心的メッセージです。タロットが告げる共鳴度が示すものに関する真実は、反映に集約されています。反映という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。反映を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1167, 116704, 11670403, '【PELE】のカードが出ました。このカードは「情熱を注ぐ」を意味します。共鳴度が示すものにおいて、隆起が極めて重要な役割を果たしています。この隆起のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。隆起という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1167, 116704, 11670404, '出たカードは【HAUMEA】。必要なものは与えられることを示す重要なカードです。共鳴度が示すものにおいて、滋養が極めて重要な役割を果たしています。この滋養のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。滋養という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1167, 116705, 11670501, '【KANE】のカードが現れました。創造を象徴し、もう一度始めることを教えてくれます。あなたの結ばれた相手が大切にすることには、創造が深く関わっています。この創造というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。創造のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、創造と共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1167, 116705, 11670502, 'タロットが告げるのは【HULU】。イリュージョンというキーワードで、無限の可能性ことを伝えています。カードが明かす結ばれた相手が大切にすることの真実において、イリュージョンは中心的な役割を果たしています。イリュージョンを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。イリュージョンのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、イリュージョンという贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1167, 116705, 11670503, '【PAHU】のカードが出ました。このカードは「ターニングポイント」を意味します。結ばれた相手が大切にすることを考える上で、タイミング・儀式は欠かせない要素となっています。タイミング・儀式の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、タイミング・儀式の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、タイミング・儀式のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1167, 116705, 11670504, 'タロットが告げるのは【MANA】。力を与えることというキーワードで、満ちているエネルギーをどう使うかことを伝えています。あなたが知りたい結ばれた相手が大切にすることの核心は、力を与えることにあります。力を与えることというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。力を与えることを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1167, 116706, 11670601, '【LEHUA】が選ばれました。「安心して前進する」がこのカードの核心的メッセージです。カードが示す強化を導く追求することの答えは、イニシエーションにあります。このイニシエーションを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、イニシエーションという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、イニシエーションの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1167, 116706, 11670602, '【MAMALAHOE】のカードです。MAMALAHOEが示すのは同情であり、自分の気持ちを最優先することの重要性です。あなたの強化を導く追求することには、同情が深く関わっています。この同情というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。同情のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、同情と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1167, 116706, 11670603, '【LEI】のカードが語りかけます。日々の暮らしを大切にという智恵を、あなたに授けようとしています。あなたの強化を導く追求することには、大切が深く関わっています。この大切というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。大切のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、大切と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1167, 116706, 11670604, '【PUA】のカードが語りかけます。目的を定め前進あるのみという智恵を、あなたに授けようとしています。強化を導く追求することを考える上で、前進は欠かせない要素となっています。前進の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、前進の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、前進のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。');
