INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1173, 117301, 11730101, '【NALU】のカードが示されました。「感情を観察する」というメッセージが込められています。今の真の想いについて、黙想のエネルギーが強く働いています。あの人は今、心の奥底で黙想に関わる深い感情を抱いているようです。表面的には見えにくいかもしれませんが、確実にその気持ちは存在しています。誠実に向き合い、お互いの心を開くことで、二人の関係は今まで以上に良い方向へと進んでいくでしょう。素直な気持ちを大切にしながら、焦らず一歩ずつ進んでください。信頼と理解を深めることが、より良い関係を築く鍵となります。'),
