INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1180, 118001, '職業的方向性を実現する究めるべきこと'),
(1180, 118002, 'ビジネス運を左右する要因'),
(1180, 118003, '職場環境の核心'),
(1180, 118004, 'あなたが感じていること'),
(1180, 118005, 'プロフェッショナルにとっての効果的な方法'),
(1180, 118006, '職業的存在が感じていること'),
(1180, 118007, 'キャリアウーマンが大切にすること'),
