INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1191, 119101, '別れた相手の復縁への願望と二人の再建'),
(1191, 119102, '復縁への意思が教えてくれること'),
(1191, 119103, '消えない想いから見える真実'),
(1191, 119104, '昔の相手にとっての準備すること'),
(1191, 119105, '関係の修復の行方'),
(1191, 119106, 'もう一度の愛としての避けるべきこと'),
