INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1192, '恋愛エネルギーから始まる新しい恋　理想の相手鑑定', '今の恋愛運詳細', '愛を信じる自分との縁の始まり。恋愛傾向や新しい恋について、ハワイアンタロットが詳しく教えます。', 10, NULL, NULL, NULL, NULL, NULL, '2026-05-31 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1192, 119201, '縁の成立の可能性'),
(1192, 119202, '恋の運命が示すもの'),
(1192, 119203, '恋の始まりを左右する要因'),
