INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1206, '選択の果てを掴む　道徳との葛藤と考えるべきこと', '真実が明らかに', '心からの想いの答えが欲しい…　選択の果てを見通したい…　秘密の恋をハワイアンタロットで占います。', 4, NULL, NULL, NULL, NULL, NULL, '2026-06-14 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1206, 120601, '深い愛情が教えてくれること'),
(1206, 120602, '想い人が感じていること'),
(1206, 120603, '関係の行方への心の声'),
(1206, 120604, '運命の終着点を導く吟味すること'),
(1206, 120605, '秘めた想いの意味と二人の未来'),
(1206, 120606, '選ぶべきことがもたらす関係の行方'),
