INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1212, '幸せを求めるあなたの選択　恋愛バイオリズムが導く出会いの実現', '完全占い', '恋愛の波が気になる…　理想との遭遇はどうなる？　ハワイアンタロットが愛を探すあなたの真実を明らかにします。', 10, NULL, NULL, NULL, NULL, NULL, '2026-06-20 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1212, 121201, '出会いを願うあなたが感じていること'),
(1212, 121202, '運命の相手を待つ自分にとっての意識すること'),
(1212, 121203, '恋愛エネルギーが教えてくれること'),
(1212, 121204, '愛に飢えたあなたが望む恋愛エネルギー'),
(1212, 121205, '出会いの運勢と運命的瞬間の関係'),
(1212, 121206, '愛を探すあなたが感じていること'),
