INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1213, 'これからの展開の真相　誠実な気持ち徹底検証', '魂の繋がりの答え', '真の気持ちが分からない…　先の見通しが不安…　ハワイアンタロットが彼女について詳しく占います。', 1, NULL, NULL, NULL, NULL, NULL, '2026-06-21 00:00:00');
