INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1219, '現在のフェーズを占う　根源的な存在の内なる喜び', '生きがいの答え', '魂の充足と現状について。深層の自己に関する生の意味を、ハワイアンタロットが徹底解明。', 7, NULL, NULL, NULL, NULL, NULL, '2026-06-27 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1219, 121901, '精神の安寧の意味と今の境遇'),
(1219, 121902, '現在の環境への道筋'),
(1219, 121903, '原初の自分の今この瞬間に対する想い'),
(1219, 121904, '生の充実を深く知る'),
(1219, 121905, '本来の姿が求める人生設計'),
(1219, 121906, '幸福感の意味と現在の位置'),
(1219, 121907, '今の段階における魂の本質の役割'),
