INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1232, '愛を探すあなたの縁の強さと出会いの実現　徹底解明', '理想の相手の核心', '愛を探すあなたとの恋の予感。出会いの運勢や縁の成立について、ハワイアンタロットが詳しく教えます。', 10, NULL, NULL, NULL, NULL, NULL, '2026-07-10 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1232, 123201, '素敵な人を探すあなたが感じていること'),
(1232, 123202, '素敵な出会いの核心'),
(1232, 123203, '出会いの成就への道筋'),
(1232, 123204, '自分自身が求める縁の始まり'),
(1232, 123205, '縁の始まりの核心');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1232, 123201, 12320101, '【PUA】が選ばれました。「目的を定め前進あるのみ」がこのカードの核心的メッセージです。素敵な人を探すあなたが感じていることを考える上で、前進は欠かせない要素となっています。前進の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、前進の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、前進のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1232, 123201, 12320102, '【AWA】が選ばれました。「無条件と見返りの違いを意識する」がこのカードの核心的メッセージです。素敵な人を探すあなたが感じていることにおいて、供物・生贄が極めて重要な役割を果たしています。この供物・生贄のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。供物・生贄という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1232, 123201, 12320103, '【PU’OLO】のカードが現れました。解放を象徴し、不必要なものを手放すことを教えてくれます。素敵な人を探すあなたが感じていることに対するハワイアンタロットの答えは明確です。それは解放です。この解放という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。解放は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で解放を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1232, 123201, 12320104, 'カードに現れたのは【MANA】。テーマは「満ちているエネルギーをどう使うか」です。あなたが知りたい素敵な人を探すあなたが感じていることの核心は、力を与えることにあります。力を与えることというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。力を与えることを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1232, 123202, 12320201, '【KA WAI A KA PILILIKO】のカードが現れました。反映を象徴し、ありのままを受け入れることを教えてくれます。あなたが知りたい素敵な出会いの核心の核心は、反映にあります。反映というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。反映を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1232, 123202, 12320202, '【LONO/KAMAPUA’A】が選ばれました。「やり切ることで収穫に繋がる」がこのカードの核心的メッセージです。素敵な出会いの核心を考える上で、収穫/豊穣は欠かせない要素となっています。収穫/豊穣の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、収穫/豊穣の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、収穫/豊穣のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1232, 123202, 12320203, '【PUHI】のカードが語りかけます。新しい創造のための破壊という智恵を、あなたに授けようとしています。あなたの素敵な出会いの核心には、崩壊が深く関わっています。この崩壊というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。崩壊のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、崩壊と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1232, 123202, 12320204, '【ANO’ANO】のカードが語りかけます。思考が現実を創る／全てを受けとめるという智恵を、あなたに授けようとしています。あなたの素敵な出会いの核心には、意思が深く関わっています。この意思というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。意思のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、意思と共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1232, 123203, 12320301, 'タロットが告げるのは【TI】。浄化というキーワードで、感情、肉体、精神の浄化をすることを伝えています。あなたが知りたい出会いの成就への道筋の核心は、浄化にあります。浄化というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。浄化を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1232, 123203, 12320302, '【MAUI】のカードが語りかけます。失敗を恐れずチャレンジするという智恵を、あなたに授けようとしています。あなたの出会いの成就への道筋には、トリックスターが深く関わっています。このトリックスターというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。トリックスターのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、トリックスターと共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1232, 123203, 12320303, '【HAUMEA】のカードが出ました。このカードは「必要なものは与えられる」を意味します。出会いの成就への道筋において最も大切なのは、滋養という概念を正しく認識することです。滋養は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて滋養と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、滋養と共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1232, 123203, 12320304, 'タロットが告げるのは【PUEO】。導きというキーワードで、自然の流れに身をまかせることを伝えています。出会いの成就への道筋を考える上で、導きは欠かせない要素となっています。導きの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、導きの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、導きのメッセージを受け取りましょう。道は必ず開けます。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1232, 123204, 12320401, '【ALOHA】のカードです。ALOHAが示すのは愛であり、真の愛を学ぶことの重要性です。自分自身が求める縁の始まりにおいて最も大切なのは、愛という概念を正しく認識することです。愛は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて愛と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、愛と共に成長していきましょう。未来は明るく輝いています。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1232, 123204, 12320402, '【MENEHUNE】のカードが示されました。「コツコツと積み重ねる」というメッセージが込められています。あなたが知りたい自分自身が求める縁の始まりの核心は、持続にあります。持続というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。持続を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1232, 123204, 12320403, '【MAMALAHOE】のカードが語りかけます。自分の気持ちを最優先するという智恵を、あなたに授けようとしています。タロットが告げる自分自身が求める縁の始まりに関する真実は、同情に集約されています。同情という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。同情を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1232, 123204, 12320404, '出たカードは【KANALOA】。基盤づくりをすることを示す重要なカードです。カードが示す自分自身が求める縁の始まりの答えは、基盤にあります。この基盤を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、基盤という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、基盤の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1232, 123205, 12320501, '【HO’OPONOPONO】のカードが出ました。このカードは「コミュニケーションを大切にする」を意味します。縁の始まりの核心に対するハワイアンタロットの答えは明確です。それはコミュニケーションです。このコミュニケーションという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。コミュニケーションは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でコミュニケーションを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1232, 123205, 12320502, '【HI’IAKA】のカードが示されました。「望む結果をイメージする」というメッセージが込められています。あなたが知りたい縁の始まりの核心の核心は、献身にあります。献身というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。献身を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1232, 123205, 12320503, '【KU/HINA】のカードが語りかけます。活動する/休息するという智恵を、あなたに授けようとしています。カードが示す縁の始まりの核心の答えは、戦い/癒しにあります。この戦い/癒しを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、戦い/癒しという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、戦い/癒しの導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1232, 123205, 12320504, '【POLI’AHU】のカードが現れました。静けさの愛撫を象徴し、不動の静けさからくる強さことを教えてくれます。あなたが知りたい縁の始まりの核心の核心は、静けさの愛撫にあります。静けさの愛撫というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。静けさの愛撫を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。');
