INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1235, '心情の実像の全て　今後の流れを完全予測', '本心からの想いの真相', '誠実な感情と先々の見通しについて。知りたい相手に関する感情の本体を、ハワイアンタロットが徹底解明。', 3, NULL, NULL, NULL, NULL, NULL, '2026-07-13 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1235, 123501, '気がかりな人が大切にすること'),
(1235, 123502, '想いの真実における重要ポイント'),
(1235, 123503, '心の本音の全体像'),
(1235, 123504, '心の奥底が示すもの'),
(1235, 123505, '先々の見通しを実現する効果的な方法'),
(1235, 123506, '見守る相手が望む心の奥底'),
