INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1248, '心の準備の全体像　人生設計完全解読', '人生設計完全予測', '心の準備が分からない…　生活の展望が不安…　ハワイアンタロットが共にする人について詳しく占います。', 6, NULL, NULL, NULL, NULL, NULL, '2026-07-26 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1248, 124801, '結婚生活への道筋'),
(1248, 124802, '人生の伴侶における話し合うこと'),
(1248, 124803, 'パートナーの心の準備と結婚の行方'),
(1248, 124804, '将来設計の展望'),
(1248, 124805, '結婚の本質の全体像'),
(1248, 124806, '結婚生活の真相');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1248, 124801, 12480101, '出たカードは【AWA】。無条件と見返りの違いを意識することを示す重要なカードです。カードが示す結婚生活への道筋の答えは、供物・生贄にあります。この供物・生贄を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、供物・生贄という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、供物・生贄の導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1248, 124801, 12480102, 'ハワイアンタロットの【HUNA】。秘密のエネルギーを持ち、可能性を禁じないことを説いています。タロットが告げる結婚生活への道筋に関する真実は、秘密に集約されています。秘密という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。秘密を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1248, 124801, 12480103, '【MAMALAHOE】のカードが現れました。同情を象徴し、自分の気持ちを最優先することを教えてくれます。カードが明かす結婚生活への道筋の真実において、同情は中心的な役割を果たしています。同情を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。同情のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、同情という贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1248, 124801, 12480104, 'タロットが告げるのは【LA’AMAOMAO】。償いというキーワードで、失敗は成功の学びことを伝えています。あなたが知りたい結婚生活への道筋の核心は、償いにあります。償いというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。償いを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1248, 124802, 12480201, '【PO/AO】のカードが示されました。「深層心理を探る」というメッセージが込められています。タロットが告げる人生の伴侶における話し合うことに関する真実は、無意識/意識に集約されています。無意識/意識という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。無意識/意識を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1248, 124802, 12480202, 'カードに現れたのは【I’O】。テーマは「全ては必然で起こっている」です。人生の伴侶における話し合うことにおいて、ミステリーが極めて重要な役割を果たしています。このミステリーのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。ミステリーという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1248, 124802, 12480203, '出たカードは【HAUMEA】。必要なものは与えられることを示す重要なカードです。人生の伴侶における話し合うことに対するハワイアンタロットの答えは明確です。それは滋養です。この滋養という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。滋養は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で滋養を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1248, 124802, 12480204, '出たカードは【POHAKU】。人間関係を見直すことを示す重要なカードです。人生の伴侶における話し合うことに秘められた真実は、関係によって明らかになります。愛に関わるあらゆる疑問や不安は、関係という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、関係を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。関係さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1248, 124803, 12480301, '出たカードは【HINA】。自分自身を成長させることを示す重要なカードです。パートナーの心の準備と結婚の行方に秘められた真実は、養いによって明らかになります。愛に関わるあらゆる疑問や不安は、養いという視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、養いを中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。養いさえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1248, 124803, 12480302, 'ハワイアンタロットの【ALOHA】。愛のエネルギーを持ち、真の愛を学ぶことを説いています。パートナーの心の準備と結婚の行方に秘められた真実は、愛によって明らかになります。愛に関わるあらゆる疑問や不安は、愛という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、愛を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。愛さえ忘れなければ、二人の未来は明るく輝くでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1248, 124803, 12480303, '出たカードは【KU/HINA】。活動する/休息することを示す重要なカードです。カードが示すパートナーの心の準備と結婚の行方の答えは、戦い/癒しにあります。この戦い/癒しを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、戦い/癒しという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、戦い/癒しの導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1248, 124803, 12480304, 'カードに現れたのは【HULU】。テーマは「無限の可能性」です。恋愛におけるパートナーの心の準備と結婚の行方を占う上で、イリュージョン以上に重要なものはありません。イリュージョンは、二人の関係を支える基盤であり、愛を育む土壌です。このイリュージョンを大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、イリュージョンがあれば必ず乗り越えられます。愛を信じ、イリュージョンと共に歩んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1248, 124804, 12480401, '【PU’OLO】のカードです。PU’OLOが示すのは解放であり、不必要なものを手放すことの重要性です。将来設計の展望に秘められた真実は、解放によって明らかになります。愛に関わるあらゆる疑問や不安は、解放という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、解放を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。解放さえ忘れなければ、二人の未来は明るく輝くでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1248, 124804, 12480402, 'ハワイアンタロットの【LONO/KAMAPUA’A】。収穫/豊穣のエネルギーを持ち、やり切ることで収穫に繋がることを説いています。カードが示す将来設計の展望の答えは、収穫/豊穣にあります。この収穫/豊穣を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、収穫/豊穣という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、収穫/豊穣の導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1248, 124804, 12480403, '【TAPA】のカードです。TAPAが示すのは結合であり、パートナーシップ、距離感を考えることの重要性です。将来設計の展望において、結合が極めて重要な役割を果たしています。この結合のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。結合という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1248, 124804, 12480404, '出たカードは【LEHUA】。安心して前進することを示す重要なカードです。カードが示す将来設計の展望の答えは、イニシエーションにあります。このイニシエーションを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、イニシエーションという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、イニシエーションの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1248, 124805, 12480501, '【POLI’AHU】のカードです。POLI’AHUが示すのは静けさの愛撫であり、不動の静けさからくる強さことの重要性です。あなたの結婚の本質の全体像には、静けさの愛撫が深く関わっています。この静けさの愛撫というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。静けさの愛撫のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、静けさの愛撫と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1248, 124805, 12480502, '出たカードは【TI】。感情、肉体、精神の浄化をすることを示す重要なカードです。あなたの結婚の本質の全体像には、浄化が深く関わっています。この浄化というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。浄化のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、浄化と共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1248, 124805, 12480503, '【KANALOA】のカードが語りかけます。基盤づくりをするという智恵を、あなたに授けようとしています。結婚の本質の全体像において、基盤が極めて重要な役割を果たしています。この基盤のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。基盤という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1248, 124805, 12480504, '【PUHI】のカードが語りかけます。新しい創造のための破壊という智恵を、あなたに授けようとしています。カードが明かす結婚の本質の全体像の真実において、崩壊は中心的な役割を果たしています。崩壊を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。崩壊のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、崩壊という贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1248, 124806, 12480601, '【ANO’ANO】のカードです。ANO’ANOが示すのは意思であり、思考が現実を創る／全てを受けとめることの重要性です。結婚生活の真相に対するハワイアンタロットの答えは明確です。それは意思です。この意思という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。意思は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で意思を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1248, 124806, 12480602, '【PAHU】のカードが語りかけます。ターニングポイントという智恵を、あなたに授けようとしています。結婚生活の真相を考える上で、タイミング・儀式は欠かせない要素となっています。タイミング・儀式の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、タイミング・儀式の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、タイミング・儀式のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1248, 124806, 12480603, '【MANO】のカードが出ました。このカードは「リーダーシップを取る」を意味します。結婚生活の真相において、リーダーシップが極めて重要な役割を果たしています。このリーダーシップのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。リーダーシップという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1248, 124806, 12480604, '【PAPA&WAKEA】のカードです。PAPA&WAKEAが示すのは苦闘・全体であり、葛藤の中から新しいものを生み出すことの重要性です。結婚生活の真相において、苦闘・全体が極めて重要な役割を果たしています。この苦闘・全体のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。苦闘・全体という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。');
