INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1249, '心の平安と進むべき方針　現時点を引き寄せる', '人生の意義の全て', '充実感と現在地点について。本来の存在に関する人生の目的を、ハワイアンタロットが徹底解明。', 7, NULL, NULL, NULL, NULL, NULL, '2026-07-27 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1249, 124901, '本当のあなたが望む心の平安'),
(1249, 124902, '深い喜びの深層'),
(1249, 124903, '現時点を導く進むべき道'),
(1249, 124904, '真の姿の存在価値'),
(1249, 124905, '純粋な自己が大切にすること'),
(1249, 124906, '現在のフェーズの可能性');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1249, 124901, 12490101, '【LEHUA】のカードです。LEHUAが示すのはイニシエーションであり、安心して前進することの重要性です。あなたの本当のあなたが望む心の平安には、イニシエーションが深く関わっています。このイニシエーションというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。イニシエーションのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、イニシエーションと共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1249, 124901, 12490102, '【LA’AMAOMAO】が選ばれました。「失敗は成功の学び」がこのカードの核心的メッセージです。あなたの本当のあなたが望む心の平安には、償いが深く関わっています。この償いというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。償いのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、償いと共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1249, 124901, 12490103, 'タロットが告げるのは【LAKA】。インスピレーションというキーワードで、直感を大切にすることを伝えています。本当のあなたが望む心の平安において最も大切なのは、インスピレーションという概念を正しく認識することです。インスピレーションは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてインスピレーションと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、インスピレーションと共に成長していきましょう。未来は明るく輝いています。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1249, 124901, 12490104, 'ハワイアンタロットの【LAMAKU】。勝利のエネルギーを持ち、意思の力で喜びを手にすることを説いています。タロットが告げる本当のあなたが望む心の平安に関する真実は、勝利に集約されています。勝利という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。勝利を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1249, 124902, 12490201, '【PO/AO】のカードが出ました。このカードは「深層心理を探る」を意味します。カードが明かす深い喜びの深層の真実において、無意識/意識は中心的な役割を果たしています。無意識/意識を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。無意識/意識のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、無意識/意識という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1249, 124902, 12490202, '【PAHU】が選ばれました。「ターニングポイント」がこのカードの核心的メッセージです。あなたが知りたい深い喜びの深層の核心は、タイミング・儀式にあります。タイミング・儀式というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。タイミング・儀式を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1249, 124902, 12490203, '出たカードは【PELE】。情熱を注ぐことを示す重要なカードです。深い喜びの深層を考える上で、隆起は欠かせない要素となっています。隆起の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、隆起の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、隆起のメッセージを受け取りましょう。道は必ず開けます。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1249, 124902, 12490204, 'タロットが告げるのは【HAUMEA】。滋養というキーワードで、必要なものは与えられることを伝えています。あなたが知りたい深い喜びの深層の核心は、滋養にあります。滋養というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。滋養を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1249, 124903, 12490301, '【ALOHA】が選ばれました。「真の愛を学ぶ」がこのカードの核心的メッセージです。キャリアや人生設計における現時点を導く進むべき道を考える際、愛は最重要ファクターです。成功者に共通するのは、この愛を深く理解し、実践していることです。愛という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、愛を磨き続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1249, 124903, 12490302, '【NALU】のカードが語りかけます。感情を観察するという智恵を、あなたに授けようとしています。キャリアや人生設計における現時点を導く進むべき道を考える際、黙想は最重要ファクターです。成功者に共通するのは、この黙想を深く理解し、実践していることです。黙想という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、黙想を磨き続けてください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1249, 124903, 12490303, '【LONO/KAMAPUA’A】が選ばれました。「やり切ることで収穫に繋がる」がこのカードの核心的メッセージです。キャリアや人生設計における現時点を導く進むべき道を考える際、収穫/豊穣は最重要ファクターです。成功者に共通するのは、この収穫/豊穣を深く理解し、実践していることです。収穫/豊穣という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、収穫/豊穣を磨き続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1249, 124903, 12490304, '【MANA】のカードが出ました。このカードは「満ちているエネルギーをどう使うか」を意味します。キャリアや人生設計における現時点を導く進むべき道を考える際、力を与えることは最重要ファクターです。成功者に共通するのは、この力を与えることを深く理解し、実践していることです。力を与えることという軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、力を与えることを磨き続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1249, 124904, 12490401, '【HINA】が選ばれました。「自分自身を成長させる」がこのカードの核心的メッセージです。真の姿の存在価値を考える上で、養いは欠かせない要素となっています。養いの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、養いの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、養いのメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1249, 124904, 12490402, 'ハワイアンタロットの【POLI’AHU】。静けさの愛撫のエネルギーを持ち、不動の静けさからくる強さことを説いています。真の姿の存在価値に対するハワイアンタロットの答えは明確です。それは静けさの愛撫です。この静けさの愛撫という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。静けさの愛撫は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で静けさの愛撫を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1249, 124904, 12490403, '【MAUI】が選ばれました。「失敗を恐れずチャレンジする」がこのカードの核心的メッセージです。あなたが知りたい真の姿の存在価値の核心は、トリックスターにあります。トリックスターというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。トリックスターを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1249, 124904, 12490404, '【MENEHUNE】のカードが現れました。持続を象徴し、コツコツと積み重ねることを教えてくれます。真の姿の存在価値に対するハワイアンタロットの答えは明確です。それは持続です。この持続という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。持続は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で持続を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1249, 124905, 12490501, 'ハワイアンタロットの【TAPA】。結合のエネルギーを持ち、パートナーシップ、距離感を考えることを説いています。キャリアや人生設計における純粋な自己が大切にすることを考える際、結合は最重要ファクターです。成功者に共通するのは、この結合を深く理解し、実践していることです。結合という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、結合を磨き続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1249, 124905, 12490502, '出たカードは【KU/HINA】。活動する/休息することを示す重要なカードです。純粋な自己が大切にすることに関して、カードは戦い/癒しという重要なメッセージを伝えています。あなたが今必要としているのは、この戦い/癒しを深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。戦い/癒しの真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1249, 124905, 12490503, '【HO’OPONOPONO】のカードが語りかけます。コミュニケーションを大切にするという智恵を、あなたに授けようとしています。あなたの純粋な自己が大切にすることには、コミュニケーションが深く関わっています。このコミュニケーションというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。コミュニケーションのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、コミュニケーションと共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1249, 124905, 12490504, '【HUNA】のカードが現れました。秘密を象徴し、可能性を禁じないことを教えてくれます。キャリアや人生設計における純粋な自己が大切にすることを考える際、秘密は最重要ファクターです。成功者に共通するのは、この秘密を深く理解し、実践していることです。秘密という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、秘密を磨き続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1249, 124906, 12490601, '【POHAKU】のカードが語りかけます。人間関係を見直すという智恵を、あなたに授けようとしています。現在のフェーズの可能性において最も大切なのは、関係という概念を正しく認識することです。関係は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて関係と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、関係と共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1249, 124906, 12490602, '【PUHI】が選ばれました。「新しい創造のための破壊」がこのカードの核心的メッセージです。カードが明かす現在のフェーズの可能性の真実において、崩壊は中心的な役割を果たしています。崩壊を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。崩壊のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、崩壊という贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1249, 124906, 12490603, '【LEI】のカードが現れました。大切を象徴し、日々の暮らしを大切にことを教えてくれます。現在のフェーズの可能性という人生の重要なテーマにおいて、大切は羅針盤となります。人生の岐路に立ったとき、大切を指針とすることで、正しい選択ができるでしょう。大切は、あなたの使命や天命とも深く関わっています。この大切を追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、大切に従って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1249, 124906, 12490604, 'カードに現れたのは【ANO’ANO】。テーマは「思考が現実を創る／全てを受けとめる」です。現在のフェーズの可能性に関して、カードは意思という重要なメッセージを伝えています。あなたが今必要としているのは、この意思を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。意思の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。');
