INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1250, '職場環境の核心　仕事観から職業的方向性へ', '完全占い', '職業への想いを解明し、キャリアビジョンを予測。働くあなたについて、ハワイアンタロットが仕事運を占います。', 8, NULL, NULL, NULL, NULL, NULL, '2026-07-28 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1250, 125001, '職業運の核心'),
(1250, 125002, 'キャリア観の意味と職業的方向性'),
(1250, 125003, '仕事への信念が示すもの'),
(1250, 125004, 'キャリア形成における究めるべきこと'),
(1250, 125005, '実践すべきことがもたらす仕事の可能性');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1250, 125001, 12500101, '【PELE】のカードが現れました。隆起を象徴し、情熱を注ぐことを教えてくれます。職業運の核心という人生の重要なテーマにおいて、隆起は羅針盤となります。人生の岐路に立ったとき、隆起を指針とすることで、正しい選択ができるでしょう。隆起は、あなたの使命や天命とも深く関わっています。この隆起を追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、隆起に従って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1250, 125001, 12500102, '【PAHU】のカードが語りかけます。ターニングポイントという智恵を、あなたに授けようとしています。キャリアや人生設計における職業運の核心を考える際、タイミング・儀式は最重要ファクターです。成功者に共通するのは、このタイミング・儀式を深く理解し、実践していることです。タイミング・儀式という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、タイミング・儀式を磨き続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1250, 125001, 12500103, '【LAKA】のカードが現れました。インスピレーションを象徴し、直感を大切にすることを教えてくれます。カードが示す職業運の核心の答えは、インスピレーションにあります。このインスピレーションを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、インスピレーションという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、インスピレーションの導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1250, 125001, 12500104, '【KANE】のカードです。KANEが示すのは創造であり、もう一度始めることの重要性です。タロットが告げる職業運の核心に関する真実は、創造に集約されています。創造という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。創造を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1250, 125002, 12500201, 'ハワイアンタロットの【HAUMEA】。滋養のエネルギーを持ち、必要なものは与えられることを説いています。タロットが告げるキャリア観の意味と職業的方向性に関する真実は、滋養に集約されています。滋養という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。滋養を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1250, 125002, 12500202, '【PU’OLO】のカードが語りかけます。不必要なものを手放すという智恵を、あなたに授けようとしています。あなたが知りたいキャリア観の意味と職業的方向性の核心は、解放にあります。解放というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。解放を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1250, 125002, 12500203, '出たカードは【TAPA】。パートナーシップ、距離感を考えることを示す重要なカードです。キャリアや人生設計におけるキャリア観の意味と職業的方向性を考える際、結合は最重要ファクターです。成功者に共通するのは、この結合を深く理解し、実践していることです。結合という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、結合を磨き続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1250, 125002, 12500204, '【MO’O】のカードが語りかけます。不安が成功を妨げるという智恵を、あなたに授けようとしています。キャリア観の意味と職業的方向性に対するハワイアンタロットの答えは明確です。それは罠です。この罠という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。罠は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で罠を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1250, 125003, 12500301, '【PAPA&WAKEA】のカードが語りかけます。葛藤の中から新しいものを生み出すという智恵を、あなたに授けようとしています。カードが明かす仕事への信念が示すものの真実において、苦闘・全体は中心的な役割を果たしています。苦闘・全体を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。苦闘・全体のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、苦闘・全体という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1250, 125003, 12500302, 'ハワイアンタロットの【POHAKU】。関係のエネルギーを持ち、人間関係を見直すことを説いています。カードが示す仕事への信念が示すものの答えは、関係にあります。この関係を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、関係という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、関係の導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1250, 125003, 12500303, '【MENEHUNE】のカードです。MENEHUNEが示すのは持続であり、コツコツと積み重ねることの重要性です。あなたが知りたい仕事への信念が示すものの核心は、持続にあります。持続というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。持続を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1250, 125003, 12500304, '【WA’A】のカードが現れました。移動を象徴し、大きな変化が訪れることを教えてくれます。仕事への信念が示すものを考える上で、移動は欠かせない要素となっています。移動の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、移動の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、移動のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1250, 125004, 12500401, 'タロットが告げるのは【KANALOA】。基盤というキーワードで、基盤づくりをすることを伝えています。あなたのキャリア形成における究めるべきことには、基盤が深く関わっています。この基盤というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。基盤のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、基盤と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1250, 125004, 12500402, '【LONO/KAMAPUA’A】のカードです。LONO/KAMAPUA’Aが示すのは収穫/豊穣であり、やり切ることで収穫に繋がることの重要性です。カードが明かすキャリア形成における究めるべきことの真実において、収穫/豊穣は中心的な役割を果たしています。収穫/豊穣を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。収穫/豊穣のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、収穫/豊穣という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1250, 125004, 12500403, '【HO’OPONOPONO】のカードです。HO’OPONOPONOが示すのはコミュニケーションであり、コミュニケーションを大切にすることの重要性です。キャリア形成における究めるべきことに対するハワイアンタロットの答えは明確です。それはコミュニケーションです。このコミュニケーションという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。コミュニケーションは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でコミュニケーションを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1250, 125004, 12500404, '【NAUPAKA】のカードが現れました。別離を象徴し、切り離すことを教えてくれます。キャリア形成における究めるべきことを考える上で、別離は欠かせない要素となっています。別離の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、別離の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、別離のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1250, 125005, 12500501, '出たカードは【HULU】。無限の可能性ことを示す重要なカードです。カードが示す実践すべきことがもたらす仕事の可能性の答えは、イリュージョンにあります。このイリュージョンを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、イリュージョンという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、イリュージョンの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1250, 125005, 12500502, '出たカードは【HUNA】。可能性を禁じないことを示す重要なカードです。タロットが告げる実践すべきことがもたらす仕事の可能性に関する真実は、秘密に集約されています。秘密という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。秘密を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1250, 125005, 12500503, '【LEI】のカードです。LEIが示すのは大切であり、日々の暮らしを大切にことの重要性です。実践すべきことがもたらす仕事の可能性に関して、カードは大切という重要なメッセージを伝えています。あなたが今必要としているのは、この大切を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。大切の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1250, 125005, 12500504, '【POLI’AHU】が選ばれました。「不動の静けさからくる強さ」がこのカードの核心的メッセージです。実践すべきことがもたらす仕事の可能性に関して、カードは静けさの愛撫という重要なメッセージを伝えています。あなたが今必要としているのは、この静けさの愛撫を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。静けさの愛撫の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。');
