INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1253, '二人の選択　率直な想いが導く二人の行方', '描く未来図予測', '待ち受ける未来への不安を解消。二人の本音を、ハワイアンタロットが愛の確認として鑑定。', 1, NULL, NULL, NULL, NULL, NULL, '2026-07-31 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1253, 125301, '永続する愛における重要ポイント'),
(1253, 125302, '率直な想いの意味とこれからの展開'),
(1253, 125303, '待ち受ける未来におけるその人の役割'),
(1253, 125304, '二人の幸せを左右する要因'),
(1253, 125305, '永遠の絆としての高めるべきこと'),
(1253, 125306, '運命の道筋を導く行うべきこと');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1253, 125301, 12530101, 'タロットが告げるのは【HI’IAKA】。献身というキーワードで、望む結果をイメージすることを伝えています。永続する愛における重要ポイントに秘められた真実は、献身によって明らかになります。愛に関わるあらゆる疑問や不安は、献身という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、献身を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。献身さえ忘れなければ、二人の未来は明るく輝くでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1253, 125301, 12530102, '【PELE】のカードです。PELEが示すのは隆起であり、情熱を注ぐことの重要性です。永続する愛における重要ポイントに秘められた真実は、隆起によって明らかになります。愛に関わるあらゆる疑問や不安は、隆起という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、隆起を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。隆起さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1253, 125301, 12530103, '【KANE】のカードです。KANEが示すのは創造であり、もう一度始めることの重要性です。カードが示す永続する愛における重要ポイントの答えは、創造にあります。この創造を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、創造という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、創造の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1253, 125301, 12530104, '【ANUENUE】のカードが出ました。このカードは「隠れた部分に光をあてる」を意味します。恋愛における永続する愛における重要ポイントを占う上で、変容以上に重要なものはありません。変容は、二人の関係を支える基盤であり、愛を育む土壌です。この変容を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、変容があれば必ず乗り越えられます。愛を信じ、変容と共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1253, 125302, 12530201, '【TI】のカードです。TIが示すのは浄化であり、感情、肉体、精神の浄化をすることの重要性です。恋愛における率直な想いの意味とこれからの展開を占う上で、浄化以上に重要なものはありません。浄化は、二人の関係を支える基盤であり、愛を育む土壌です。この浄化を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、浄化があれば必ず乗り越えられます。愛を信じ、浄化と共に歩んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1253, 125302, 12530202, '【WA’A】のカードが語りかけます。大きな変化が訪れるという智恵を、あなたに授けようとしています。カードが示す率直な想いの意味とこれからの展開の答えは、移動にあります。この移動を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、移動という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、移動の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1253, 125302, 12530203, '【HULU】が選ばれました。「無限の可能性」がこのカードの核心的メッセージです。タロットが告げる率直な想いの意味とこれからの展開に関する真実は、イリュージョンに集約されています。イリュージョンという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。イリュージョンを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1253, 125302, 12530204, '【HO’OPONOPONO】のカードが出ました。このカードは「コミュニケーションを大切にする」を意味します。カードが明かす率直な想いの意味とこれからの展開の真実において、コミュニケーションは中心的な役割を果たしています。コミュニケーションを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。コミュニケーションのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、コミュニケーションという贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1253, 125303, 12530301, '【KU/HINA】のカードが出ました。このカードは「活動する/休息する」を意味します。カードが示す待ち受ける未来におけるその人の役割の答えは、戦い/癒しにあります。この戦い/癒しを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、戦い/癒しという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、戦い/癒しの導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1253, 125303, 12530302, '【MANO】のカードです。MANOが示すのはリーダーシップであり、リーダーシップを取ることの重要性です。あなたの待ち受ける未来におけるその人の役割には、リーダーシップが深く関わっています。このリーダーシップというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。リーダーシップのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、リーダーシップと共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1253, 125303, 12530303, '【LAMAKU】が選ばれました。「意思の力で喜びを手にする」がこのカードの核心的メッセージです。あなたが知りたい待ち受ける未来におけるその人の役割の核心は、勝利にあります。勝利というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。勝利を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1253, 125303, 12530304, 'カードに現れたのは【HAUMEA】。テーマは「必要なものは与えられる」です。待ち受ける未来におけるその人の役割を考える上で、滋養は欠かせない要素となっています。滋養の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、滋養の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、滋養のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1253, 125304, 12530401, '出たカードは【PAHU】。ターニングポイントことを示す重要なカードです。タロットが告げる二人の幸せを左右する要因に関する真実は、タイミング・儀式に集約されています。タイミング・儀式という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。タイミング・儀式を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1253, 125304, 12530402, '【AWA】のカードが示されました。「無条件と見返りの違いを意識する」というメッセージが込められています。タロットが告げる二人の幸せを左右する要因に関する真実は、供物・生贄に集約されています。供物・生贄という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。供物・生贄を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1253, 125304, 12530403, '【TAPA】のカードが現れました。結合を象徴し、パートナーシップ、距離感を考えることを教えてくれます。二人の幸せを左右する要因に秘められた真実は、結合によって明らかになります。愛に関わるあらゆる疑問や不安は、結合という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、結合を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。結合さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1253, 125304, 12530404, 'ハワイアンタロットの【PUA】。前進のエネルギーを持ち、目的を定め前進あるのみことを説いています。恋愛における二人の幸せを左右する要因を占う上で、前進以上に重要なものはありません。前進は、二人の関係を支える基盤であり、愛を育む土壌です。この前進を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、前進があれば必ず乗り越えられます。愛を信じ、前進と共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1253, 125305, 12530501, 'カードに現れたのは【NALU】。テーマは「感情を観察する」です。カードが示す永遠の絆としての高めるべきことの答えは、黙想にあります。この黙想を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、黙想という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、黙想の導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1253, 125305, 12530502, 'ハワイアンタロットの【ANO’ANO】。意思のエネルギーを持ち、思考が現実を創る／全てを受けとめることを説いています。永遠の絆としての高めるべきことに対するハワイアンタロットの答えは明確です。それは意思です。この意思という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。意思は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で意思を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1253, 125305, 12530503, 'カードに現れたのは【LONO/KAMAPUA’A】。テーマは「やり切ることで収穫に繋がる」です。永遠の絆としての高めるべきことに秘められた真実は、収穫/豊穣によって明らかになります。愛に関わるあらゆる疑問や不安は、収穫/豊穣という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、収穫/豊穣を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。収穫/豊穣さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1253, 125305, 12530504, '【PUEO】が選ばれました。「自然の流れに身をまかせる」がこのカードの核心的メッセージです。タロットが告げる永遠の絆としての高めるべきことに関する真実は、導きに集約されています。導きという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。導きを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1253, 125306, 12530601, 'カードに現れたのは【PAPA&WAKEA】。テーマは「葛藤の中から新しいものを生み出す」です。カードが示す運命の道筋を導く行うべきことの答えは、苦闘・全体にあります。この苦闘・全体を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、苦闘・全体という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、苦闘・全体の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1253, 125306, 12530602, '出たカードは【LEI】。日々の暮らしを大切にことを示す重要なカードです。カードが示す運命の道筋を導く行うべきことの答えは、大切にあります。この大切を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、大切という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、大切の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1253, 125306, 12530603, '【MENEHUNE】のカードが示されました。「コツコツと積み重ねる」というメッセージが込められています。運命の道筋を導く行うべきことに秘められた真実は、持続によって明らかになります。愛に関わるあらゆる疑問や不安は、持続という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、持続を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。持続さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1253, 125306, 12530604, '出たカードは【POHAKU】。人間関係を見直すことを示す重要なカードです。運命の道筋を導く行うべきことを考える上で、関係は欠かせない要素となっています。関係の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、関係の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、関係のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。');
