INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1270, 'キャリア形成者の仕事の完成　仕事の将来性への道筋', '運命の扉を開く', '職業人としての自分について知りたい…　職業意識は？　キャリアゴールは？　ハワイアンタロットが、キャリア形成を詳しく鑑定します。', 8, NULL, NULL, NULL, NULL, NULL, '2026-08-17 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1270, 127001, '職業への想いの意味と仕事の先行き'),
(1270, 127002, 'キャリアウーマンが求める仕事運'),
(1270, 127003, '仕事哲学とキャリアビジョンの関係'),
(1270, 127004, '職業への想いの深層'),
(1270, 127005, '職業への想いの深層'),
