INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1277, '愛しい人との絆　発展を占う二人の秘密', '詳細鑑定', '二人だけの世界の真相を知りたい…　二人の調和度と熟成を、ハワイアンタロットが解き明かします。', 5, NULL, NULL, NULL, NULL, NULL, '2026-08-24 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1277, 127701, '二人のマッチング'),
(1277, 127702, '深めること'),
(1277, 127703, '調和の深層'),
(1277, 127704, '成熟させることがもたらす洗練'),
(1277, 127705, '強化を導く強化すること'),
(1277, 127706, '向上への充実感');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1277, 127701, 12770101, '【PU’OLO】のカードが示されました。「不必要なものを手放す」というメッセージが込められています。二人のマッチングを考える上で、解放は欠かせない要素となっています。解放の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、解放の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、解放のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1277, 127701, 12770102, '【POHAKU】のカードが現れました。関係を象徴し、人間関係を見直すことを教えてくれます。あなたの二人のマッチングには、関係が深く関わっています。この関係というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。関係のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、関係と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1277, 127701, 12770103, '【ANO’ANO】のカードが示されました。「思考が現実を創る／全てを受けとめる」というメッセージが込められています。二人のマッチングを考える上で、意思は欠かせない要素となっています。意思の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、意思の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、意思のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1277, 127701, 12770104, '【LAMAKU】のカードです。LAMAKUが示すのは勝利であり、意思の力で喜びを手にすることの重要性です。二人のマッチングに対するハワイアンタロットの答えは明確です。それは勝利です。この勝利という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。勝利は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で勝利を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1277, 127702, 12770201, '出たカードは【WA’A】。大きな変化が訪れることを示す重要なカードです。タロットが告げる深めることに関する真実は、移動に集約されています。移動という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。移動を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1277, 127702, 12770202, 'ハワイアンタロットの【ALOHA】。愛のエネルギーを持ち、真の愛を学ぶことを説いています。深めることにおいて、愛が極めて重要な役割を果たしています。この愛のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。愛という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1277, 127702, 12770203, '【PELE】のカードが示されました。「情熱を注ぐ」というメッセージが込められています。深めることに関して、カードは隆起という重要なメッセージを伝えています。あなたが今必要としているのは、この隆起を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。隆起の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1277, 127702, 12770204, 'ハワイアンタロットの【MANO】。リーダーシップのエネルギーを持ち、リーダーシップを取ることを説いています。深めることに対するハワイアンタロットの答えは明確です。それはリーダーシップです。このリーダーシップという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。リーダーシップは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でリーダーシップを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1277, 127703, 12770301, 'ハワイアンタロットの【IWI】。尊敬のエネルギーを持ち、自分も他人も大切にすることを説いています。カードが明かす調和の深層の真実において、尊敬は中心的な役割を果たしています。尊敬を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。尊敬のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、尊敬という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1277, 127703, 12770302, '【LEHUA】のカードです。LEHUAが示すのはイニシエーションであり、安心して前進することの重要性です。あなたが知りたい調和の深層の核心は、イニシエーションにあります。イニシエーションというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。イニシエーションを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1277, 127703, 12770303, '【HAUMEA】が選ばれました。「必要なものは与えられる」がこのカードの核心的メッセージです。カードが明かす調和の深層の真実において、滋養は中心的な役割を果たしています。滋養を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。滋養のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、滋養という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1277, 127703, 12770304, '【OLE】のカードが示されました。「言葉を大切にする」というメッセージが込められています。あなたの調和の深層には、物語が深く関わっています。この物語というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。物語のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、物語と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1277, 127704, 12770401, '【PUEO】のカードです。PUEOが示すのは導きであり、自然の流れに身をまかせることの重要性です。カードが示す成熟させることがもたらす洗練の答えは、導きにあります。この導きを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、導きという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、導きの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1277, 127704, 12770402, '出たカードは【TAPA】。パートナーシップ、距離感を考えることを示す重要なカードです。あなたが知りたい成熟させることがもたらす洗練の核心は、結合にあります。結合というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。結合を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1277, 127704, 12770403, '出たカードは【KA WAI A KA PILILIKO】。ありのままを受け入れることを示す重要なカードです。成熟させることがもたらす洗練に対するハワイアンタロットの答えは明確です。それは反映です。この反映という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。反映は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で反映を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1277, 127704, 12770404, 'タロットが告げるのは【LEI】。大切というキーワードで、日々の暮らしを大切にことを伝えています。タロットが告げる成熟させることがもたらす洗練に関する真実は、大切に集約されています。大切という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。大切を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1277, 127705, 12770501, 'タロットが告げるのは【PO/AO】。無意識/意識というキーワードで、深層心理を探ることを伝えています。あなたの強化を導く強化することには、無意識/意識が深く関わっています。この無意識/意識というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。無意識/意識のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、無意識/意識と共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1277, 127705, 12770502, '【HULU】のカードが示されました。「無限の可能性」というメッセージが込められています。強化を導く強化することにおいて最も大切なのは、イリュージョンという概念を正しく認識することです。イリュージョンは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてイリュージョンと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、イリュージョンと共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1277, 127705, 12770503, 'ハワイアンタロットの【LONO/KAMAPUA’A】。収穫/豊穣のエネルギーを持ち、やり切ることで収穫に繋がることを説いています。強化を導く強化することにおいて最も大切なのは、収穫/豊穣という概念を正しく認識することです。収穫/豊穣は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて収穫/豊穣と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、収穫/豊穣と共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1277, 127705, 12770504, '【MO’O】のカードが出ました。このカードは「不安が成功を妨げる」を意味します。カードが示す強化を導く強化することの答えは、罠にあります。この罠を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、罠という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、罠の導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1277, 127706, 12770601, 'カードに現れたのは【I’O】。テーマは「全ては必然で起こっている」です。タロットが告げる向上への充実感に関する真実は、ミステリーに集約されています。ミステリーという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。ミステリーを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1277, 127706, 12770602, 'タロットが告げるのは【LA’AMAOMAO】。償いというキーワードで、失敗は成功の学びことを伝えています。カードが明かす向上への充実感の真実において、償いは中心的な役割を果たしています。償いを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。償いのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、償いという贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1277, 127706, 12770603, 'タロットが告げるのは【HUNA】。秘密というキーワードで、可能性を禁じないことを伝えています。カードが明かす向上への充実感の真実において、秘密は中心的な役割を果たしています。秘密を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。秘密のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、秘密という贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1277, 127706, 12770604, '【PAPA&WAKEA】のカードが示されました。「葛藤の中から新しいものを生み出す」というメッセージが込められています。あなたが知りたい向上への充実感の核心は、苦闘・全体にあります。苦闘・全体というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。苦闘・全体を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。');
