(1287, '充実への道　愛の極致完全ガイド', '究極への道', 'マッチングが気になる…　到達はどうなる？　ハワイアンタロットが愛しい人の真実を明らかにします。', 5, NULL, NULL, NULL, NULL, NULL, '2026-09-03 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1287, 128701, '完全な調和における重要ポイント'),
(1287, 128702, '心身の融合の全体像'),
(1287, 128703, '深まりへの道筋'),
(1287, 128704, '進化の展望'),
(1287, 128705, '愛の極致における追求すること'),
