INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1300, '仕事人のワークスタイルは？　仕事の将来性を知りたい', '真実の答え', '仕事の結実が気になるあなたへ。職業への想いを含む仕事の充実を、ハワイアンタロットが完全鑑定。', 8, NULL, NULL, NULL, NULL, NULL, '2026-09-16 00:00:00');
