INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1301, '戻りたい相手との縁の復活　関係の再開完全予測', '消えない想い詳細', 'もう一度の想いの本質、復縁の展望の真相。かつての相手について、ハワイアンタロットが二度目のチャンスを完全鑑定。', 9, NULL, NULL, NULL, NULL, NULL, '2026-09-17 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1301, 130101, '縁の再生における重要ポイント'),
(1301, 130102, '復縁の可能性を実現する変えるべきこと'),
(1301, 130103, '二度目のチャンスの真相'),
(1301, 130104, '心残りの相手との復縁の展望'),
(1301, 130105, '過去の恋人の過去への想いと愛の復活'),
