INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1306, 130601, '道徳との葛藤を左右する要因'),
(1306, 130602, '本音の愛が教えてくれること'),
(1306, 130603, '関係の行方の展望'),
