INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1325, '想いの真実の結論　特定の人との先々の状況予測', '真実が明らかに', '心の本音について悩むあなたへ。当の本人の本心からの想い、未来の形をハワイアンタロットが鑑定します。', 3, NULL, NULL, NULL, NULL, NULL, '2026-10-11 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1325, 132501, '正直な想いを深く知る'),
(1325, 132502, '先行きの予測への正直な想い'),
(1325, 132503, '先々の見通しの可能性'),
(1325, 132504, '想いの深層における重要ポイント'),
(1325, 132505, '今後の成り行きを実現する妥当な行い'),
