INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1360, '仕事の充実の真実　職業的方向性はどうなる？', '仕事の成就を読む', '職業的存在の本当のキャリアへの考えは？　仕事の先行きはどうなる？　キャリア形成をハワイアンタロットが占います。', 8, NULL, NULL, NULL, NULL, NULL, '2026-11-15 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1360, 136001, 'ビジネス運の行方'),
(1360, 136002, 'プロとしてのあなたの仕事との向き合い方と仕事の先行き'),
(1360, 136003, '職業人が望む労働観'),
(1360, 136004, '職業人の視点から見た仕事への信念'),
(1360, 136005, '職業観と仕事の見通しの関係'),
