INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1367, 136701, '共鳴する相手の視点から見たケミストリー'),
(1367, 136702, 'ハーモニーを深く知る'),
(1367, 136703, '洗練への道筋'),
