INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1394, 'その人の率直な心と先の展望　徹底解明', '魂の声解明', '想いの成果について悩むあなたへ。思い続ける人の正直な感情、二人の可能性をハワイアンタロットが鑑定します。', 2, NULL, NULL, NULL, NULL, NULL, '2026-12-19 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1394, 139401, '恋する相手の関係の展開に対する想い'),
(1394, 139402, '恋の結実の行方'),
(1394, 139403, '想いの結晶における重要ポイント'),
(1394, 139404, '秘めた気持ちの深層'),
(1394, 139405, '正直な感情を深く知る'),
(1394, 139406, '慕う人が大切にすること'),
