(1410, '職業的到達点の正体　仕事の成果詳細分析', 'キャリアの確立詳細ガイド', '職業的成長を知りたい！　ワーカーのプロ意識は？　ハワイアンタロットが仕事の成就を徹底鑑定します。', 8, NULL, NULL, NULL, NULL, NULL, '2027-01-04 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1410, 141001, '仕事への信念と仕事の発展性の関係'),
(1410, 141002, '職業的な道の可能性'),
(1410, 141003, '仕事の進展への職業への想い'),
