INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1422, '縁を大切にするあなたの恋愛オーラは？　運命的瞬間を知りたい', '出会いの運の全て', '運命の相手の可能性を探る。恋愛オーラから見る出会いの運命を、ハワイアンタロットで詳しく鑑定します。', 10, NULL, NULL, NULL, NULL, NULL, '2027-01-16 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1422, 142201, '恋の磁力の深層'),
(1422, 142202, '愛を探すあなたが望む恋愛の波'),
(1422, 142203, '運命の相手を待つ自分にとっての開くこと'),
(1422, 142204, '出会いに期待するあなたの視点から見た出会いの好機'),
(1422, 142205, '幸せを求めるあなたが感じていること'),
(1422, 142206, '恋の予感の行方');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1422, 142201, 14220101, 'ハワイアンタロットの【HINA】。養いのエネルギーを持ち、自分自身を成長させることを説いています。カードが明かす恋の磁力の深層の真実において、養いは中心的な役割を果たしています。養いを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。養いのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、養いという贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1422, 142201, 14220102, '【MANA】が選ばれました。「満ちているエネルギーをどう使うか」がこのカードの核心的メッセージです。タロットが告げる恋の磁力の深層に関する真実は、力を与えることに集約されています。力を与えることという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。力を与えることを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1422, 142201, 14220103, 'カードに現れたのは【HAUMEA】。テーマは「必要なものは与えられる」です。あなたの恋の磁力の深層には、滋養が深く関わっています。この滋養というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。滋養のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、滋養と共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1422, 142201, 14220104, '【MAUI】が選ばれました。「失敗を恐れずチャレンジする」がこのカードの核心的メッセージです。恋の磁力の深層に対するハワイアンタロットの答えは明確です。それはトリックスターです。このトリックスターという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。トリックスターは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でトリックスターを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1422, 142202, 14220201, '【PO/AO】が選ばれました。「深層心理を探る」がこのカードの核心的メッセージです。愛を探すあなたが望む恋愛の波を考える上で、無意識/意識は欠かせない要素となっています。無意識/意識の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、無意識/意識の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、無意識/意識のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1422, 142202, 14220202, '【HUNA】が選ばれました。「可能性を禁じない」がこのカードの核心的メッセージです。カードが明かす愛を探すあなたが望む恋愛の波の真実において、秘密は中心的な役割を果たしています。秘密を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。秘密のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、秘密という贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1422, 142202, 14220203, '【ANO’ANO】のカードが出ました。このカードは「思考が現実を創る／全てを受けとめる」を意味します。タロットが告げる愛を探すあなたが望む恋愛の波に関する真実は、意思に集約されています。意思という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。意思を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1422, 142202, 14220204, '【PAPA&WAKEA】のカードです。PAPA&WAKEAが示すのは苦闘・全体であり、葛藤の中から新しいものを生み出すことの重要性です。愛を探すあなたが望む恋愛の波を考える上で、苦闘・全体は欠かせない要素となっています。苦闘・全体の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、苦闘・全体の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、苦闘・全体のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1422, 142203, 14220301, '出たカードは【IWI】。自分も他人も大切にすることを示す重要なカードです。運命の相手を待つ自分にとっての開くことにおいて、尊敬が極めて重要な役割を果たしています。この尊敬のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。尊敬という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1422, 142203, 14220302, '出たカードは【ANUENUE】。隠れた部分に光をあてることを示す重要なカードです。あなたが知りたい運命の相手を待つ自分にとっての開くことの核心は、変容にあります。変容というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。変容を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1422, 142203, 14220303, 'カードに現れたのは【KU/HINA】。テーマは「活動する/休息する」です。タロットが告げる運命の相手を待つ自分にとっての開くことに関する真実は、戦い/癒しに集約されています。戦い/癒しという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。戦い/癒しを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1422, 142203, 14220304, '【LA’AMAOMAO】のカードが現れました。償いを象徴し、失敗は成功の学びことを教えてくれます。運命の相手を待つ自分にとっての開くことに対するハワイアンタロットの答えは明確です。それは償いです。この償いという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。償いは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で償いを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1422, 142204, 14220401, '【NAUPAKA】が選ばれました。「切り離す」がこのカードの核心的メッセージです。あなたの出会いに期待するあなたの視点から見た出会いの好機には、別離が深く関わっています。この別離というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。別離のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、別離と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1422, 142204, 14220402, '【LAMAKU】のカードが語りかけます。意思の力で喜びを手にするという智恵を、あなたに授けようとしています。出会いに期待するあなたの視点から見た出会いの好機を考える上で、勝利は欠かせない要素となっています。勝利の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、勝利の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、勝利のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1422, 142204, 14220403, '【POHAKU】のカードが現れました。関係を象徴し、人間関係を見直すことを教えてくれます。出会いに期待するあなたの視点から見た出会いの好機において、関係が極めて重要な役割を果たしています。この関係のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。関係という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1422, 142204, 14220404, '出たカードは【PUEO】。自然の流れに身をまかせることを示す重要なカードです。出会いに期待するあなたの視点から見た出会いの好機に対するハワイアンタロットの答えは明確です。それは導きです。この導きという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。導きは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で導きを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1422, 142205, 14220501, '【TI】のカードが現れました。浄化を象徴し、感情、肉体、精神の浄化をすることを教えてくれます。あなたの幸せを求めるあなたが感じていることには、浄化が深く関わっています。この浄化というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。浄化のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、浄化と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1422, 142205, 14220502, 'ハワイアンタロットの【MENEHUNE】。持続のエネルギーを持ち、コツコツと積み重ねることを説いています。幸せを求めるあなたが感じていることを考える上で、持続は欠かせない要素となっています。持続の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、持続の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、持続のメッセージを受け取りましょう。道は必ず開けます。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1422, 142205, 14220503, '【NALU】のカードです。NALUが示すのは黙想であり、感情を観察することの重要性です。幸せを求めるあなたが感じていることにおいて、黙想が極めて重要な役割を果たしています。この黙想のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。黙想という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1422, 142205, 14220504, '【TAPA】が選ばれました。「パートナーシップ、距離感を考える」がこのカードの核心的メッセージです。カードが明かす幸せを求めるあなたが感じていることの真実において、結合は中心的な役割を果たしています。結合を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。結合のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、結合という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1422, 142206, 14220601, '【LEI】のカードが語りかけます。日々の暮らしを大切にという智恵を、あなたに授けようとしています。あなたの恋の予感の行方には、大切が深く関わっています。この大切というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。大切のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、大切と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1422, 142206, 14220602, '【POLI’AHU】のカードが出ました。このカードは「不動の静けさからくる強さ」を意味します。恋の予感の行方に対するハワイアンタロットの答えは明確です。それは静けさの愛撫です。この静けさの愛撫という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。静けさの愛撫は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で静けさの愛撫を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1422, 142206, 14220603, '【PUA】のカードです。PUAが示すのは前進であり、目的を定め前進あるのみことの重要性です。恋の予感の行方を考える上で、前進は欠かせない要素となっています。前進の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、前進の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、前進のメッセージを受け取りましょう。道は必ず開けます。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1422, 142206, 14220604, 'カードに現れたのは【HO’OPONOPONO】。テーマは「コミュニケーションを大切にする」です。恋の予感の行方に関して、カードはコミュニケーションという重要なメッセージを伝えています。あなたが今必要としているのは、このコミュニケーションを深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。コミュニケーションの真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。');
