INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1438, '婚姻関係完全版　生活の展望へのロードマップ', '分かち合う人の真実', '二人の運命が気になるあなたへ。将来への考えを含む夫婦の絆を、ハワイアンタロットが完全鑑定。', 6, NULL, NULL, NULL, NULL, NULL, '2027-02-01 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1438, 143801, '夫婦の道の真相'),
(1438, 143802, 'commitmentにおける永続する関係'),
(1438, 143803, '夫婦の道の核心'),
(1438, 143804, '真剣さから見える真実'),
(1438, 143805, '夫婦の軌跡への真剣さ'),
(1438, 143806, '人生設計への道筋'),
(1438, 143807, '永続する関係における重要ポイント'),
(1438, 143808, '将来への考えの意味と生活の展望'),
(1438, 143809, '生涯のパートナーとしての思いやること'),
(1438, 143810, '結ばれる人との家族の未来');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1438, 143801, 14380101, '【LAKA】のカードです。LAKAが示すのはインスピレーションであり、直感を大切にすることの重要性です。タロットが告げる夫婦の道の真相に関する真実は、インスピレーションに集約されています。インスピレーションという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。インスピレーションを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143801, 14380102, 'ハワイアンタロットの【HI’IAKA】。献身のエネルギーを持ち、望む結果をイメージすることを説いています。恋愛における夫婦の道の真相を占う上で、献身以上に重要なものはありません。献身は、二人の関係を支える基盤であり、愛を育む土壌です。この献身を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、献身があれば必ず乗り越えられます。愛を信じ、献身と共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143801, 14380103, '出たカードは【TAPA】。パートナーシップ、距離感を考えることを示す重要なカードです。夫婦の道の真相を考える上で、結合は欠かせない要素となっています。結合の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、結合の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、結合のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1438, 143801, 14380104, '【KU/HINA】のカードが示されました。「活動する/休息する」というメッセージが込められています。カードが明かす夫婦の道の真相の真実において、戦い/癒しは中心的な役割を果たしています。戦い/癒しを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。戦い/癒しのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、戦い/癒しという贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143802, 14380201, '【PUEO】のカードです。PUEOが示すのは導きであり、自然の流れに身をまかせることの重要性です。あなたが知りたいcommitmentにおける永続する関係の核心は、導きにあります。導きというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。導きを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143802, 14380202, '【TI】のカードです。TIが示すのは浄化であり、感情、肉体、精神の浄化をすることの重要性です。commitmentにおける永続する関係において、浄化が極めて重要な役割を果たしています。この浄化のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。浄化という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143802, 14380203, '【PELE】のカードが示されました。「情熱を注ぐ」というメッセージが込められています。あなたが知りたいcommitmentにおける永続する関係の核心は、隆起にあります。隆起というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。隆起を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143802, 14380204, '【MANA】が選ばれました。「満ちているエネルギーをどう使うか」がこのカードの核心的メッセージです。カードが明かすcommitmentにおける永続する関係の真実において、力を与えることは中心的な役割を果たしています。力を与えることを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。力を与えることのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、力を与えることという贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1438, 143803, 14380301, '【OLE】のカードです。OLEが示すのは物語であり、言葉を大切にすることの重要性です。恋愛における夫婦の道の核心を占う上で、物語以上に重要なものはありません。物語は、二人の関係を支える基盤であり、愛を育む土壌です。この物語を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、物語があれば必ず乗り越えられます。愛を信じ、物語と共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143803, 14380302, 'タロットが告げるのは【ANO’ANO】。意思というキーワードで、思考が現実を創る／全てを受けとめることを伝えています。タロットが告げる夫婦の道の核心に関する真実は、意思に集約されています。意思という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。意思を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1438, 143803, 14380303, 'カードに現れたのは【ALOHA】。テーマは「真の愛を学ぶ」です。夫婦の道の核心に秘められた真実は、愛によって明らかになります。愛に関わるあらゆる疑問や不安は、愛という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、愛を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。愛さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143803, 14380304, '【HINA】のカードが語りかけます。自分自身を成長させるという智恵を、あなたに授けようとしています。夫婦の道の核心において、養いが極めて重要な役割を果たしています。この養いのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。養いという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1438, 143804, 14380401, 'カードに現れたのは【ANUENUE】。テーマは「隠れた部分に光をあてる」です。真剣さから見える真実に対するハワイアンタロットの答えは明確です。それは変容です。この変容という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。変容は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で変容を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143804, 14380402, '【MANO】のカードが示されました。「リーダーシップを取る」というメッセージが込められています。カードが明かす真剣さから見える真実の真実において、リーダーシップは中心的な役割を果たしています。リーダーシップを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。リーダーシップのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、リーダーシップという贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143804, 14380403, '【HO’OPONOPONO】のカードです。HO’OPONOPONOが示すのはコミュニケーションであり、コミュニケーションを大切にすることの重要性です。真剣さから見える真実において、コミュニケーションが極めて重要な役割を果たしています。このコミュニケーションのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。コミュニケーションという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1438, 143804, 14380404, '【LEI】のカードです。LEIが示すのは大切であり、日々の暮らしを大切にことの重要性です。真剣さから見える真実において最も大切なのは、大切という概念を正しく認識することです。大切は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて大切と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、大切と共に成長していきましょう。未来は明るく輝いています。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1438, 143805, 14380501, '【KA WAI A KA PILILIKO】のカードが出ました。このカードは「ありのままを受け入れる」を意味します。カードが明かす夫婦の軌跡への真剣さの真実において、反映は中心的な役割を果たしています。反映を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。反映のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、反映という贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143805, 14380502, '【KANALOA】が選ばれました。「基盤づくりをする」がこのカードの核心的メッセージです。カードが明かす夫婦の軌跡への真剣さの真実において、基盤は中心的な役割を果たしています。基盤を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。基盤のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、基盤という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143805, 14380503, '【MAUI】のカードが示されました。「失敗を恐れずチャレンジする」というメッセージが込められています。カードが明かす夫婦の軌跡への真剣さの真実において、トリックスターは中心的な役割を果たしています。トリックスターを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。トリックスターのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、トリックスターという贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143805, 14380504, '【NAUPAKA】のカードが示されました。「切り離す」というメッセージが込められています。恋愛における夫婦の軌跡への真剣さを占う上で、別離以上に重要なものはありません。別離は、二人の関係を支える基盤であり、愛を育む土壌です。この別離を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、別離があれば必ず乗り越えられます。愛を信じ、別離と共に歩んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1438, 143806, 14380601, 'タロットが告げるのは【KANE】。創造というキーワードで、もう一度始めることを伝えています。人生設計への道筋を考える上で、創造は欠かせない要素となっています。創造の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、創造の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、創造のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143806, 14380602, '出たカードは【LONO/KAMAPUA’A】。やり切ることで収穫に繋がることを示す重要なカードです。人生設計への道筋に対するハワイアンタロットの答えは明確です。それは収穫/豊穣です。この収穫/豊穣という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。収穫/豊穣は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で収穫/豊穣を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143806, 14380603, '出たカードは【HULU】。無限の可能性ことを示す重要なカードです。恋愛における人生設計への道筋を占う上で、イリュージョン以上に重要なものはありません。イリュージョンは、二人の関係を支える基盤であり、愛を育む土壌です。このイリュージョンを大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、イリュージョンがあれば必ず乗り越えられます。愛を信じ、イリュージョンと共に歩んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1438, 143806, 14380604, '【WA’A】が選ばれました。「大きな変化が訪れる」がこのカードの核心的メッセージです。あなたの人生設計への道筋には、移動が深く関わっています。この移動というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。移動のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、移動と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143807, 14380701, 'カードに現れたのは【PAHU】。テーマは「ターニングポイント」です。永続する関係における重要ポイントに秘められた真実は、タイミング・儀式によって明らかになります。愛に関わるあらゆる疑問や不安は、タイミング・儀式という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、タイミング・儀式を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。タイミング・儀式さえ忘れなければ、二人の未来は明るく輝くでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143807, 14380702, 'カードに現れたのは【HAUMEA】。テーマは「必要なものは与えられる」です。永続する関係における重要ポイントにおいて最も大切なのは、滋養という概念を正しく認識することです。滋養は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて滋養と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、滋養と共に成長していきましょう。未来は明るく輝いています。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1438, 143807, 14380703, '【IWI】のカードです。IWIが示すのは尊敬であり、自分も他人も大切にすることの重要性です。恋愛における永続する関係における重要ポイントを占う上で、尊敬以上に重要なものはありません。尊敬は、二人の関係を支える基盤であり、愛を育む土壌です。この尊敬を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、尊敬があれば必ず乗り越えられます。愛を信じ、尊敬と共に歩んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1438, 143807, 14380704, 'タロットが告げるのは【PO/AO】。無意識/意識というキーワードで、深層心理を探ることを伝えています。永続する関係における重要ポイントに対するハワイアンタロットの答えは明確です。それは無意識/意識です。この無意識/意識という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。無意識/意識は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で無意識/意識を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143808, 14380801, '【POHAKU】が選ばれました。「人間関係を見直す」がこのカードの核心的メッセージです。あなたが知りたい将来への考えの意味と生活の展望の核心は、関係にあります。関係というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。関係を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1438, 143808, 14380802, '【LEHUA】のカードです。LEHUAが示すのはイニシエーションであり、安心して前進することの重要性です。タロットが告げる将来への考えの意味と生活の展望に関する真実は、イニシエーションに集約されています。イニシエーションという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。イニシエーションを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1438, 143808, 14380803, '出たカードは【PU’OLO】。不必要なものを手放すことを示す重要なカードです。恋愛における将来への考えの意味と生活の展望を占う上で、解放以上に重要なものはありません。解放は、二人の関係を支える基盤であり、愛を育む土壌です。この解放を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、解放があれば必ず乗り越えられます。愛を信じ、解放と共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143808, 14380804, 'カードに現れたのは【LA’AMAOMAO】。テーマは「失敗は成功の学び」です。将来への考えの意味と生活の展望を考える上で、償いは欠かせない要素となっています。償いの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、償いの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、償いのメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1438, 143809, 14380901, '【MAMALAHOE】が選ばれました。「自分の気持ちを最優先する」がこのカードの核心的メッセージです。生涯のパートナーとしての思いやることにおいて最も大切なのは、同情という概念を正しく認識することです。同情は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて同情と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、同情と共に成長していきましょう。未来は明るく輝いています。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143809, 14380902, '【PUA】のカードが現れました。前進を象徴し、目的を定め前進あるのみことを教えてくれます。カードが示す生涯のパートナーとしての思いやることの答えは、前進にあります。この前進を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、前進という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、前進の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1438, 143809, 14380903, '【NALU】が選ばれました。「感情を観察する」がこのカードの核心的メッセージです。生涯のパートナーとしての思いやることに秘められた真実は、黙想によって明らかになります。愛に関わるあらゆる疑問や不安は、黙想という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、黙想を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。黙想さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143809, 14380904, '出たカードは【MENEHUNE】。コツコツと積み重ねることを示す重要なカードです。タロットが告げる生涯のパートナーとしての思いやることに関する真実は、持続に集約されています。持続という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。持続を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143810, 14381001, '【LAMAKU】のカードが語りかけます。意思の力で喜びを手にするという智恵を、あなたに授けようとしています。結ばれる人との家族の未来に関して、カードは勝利という重要なメッセージを伝えています。あなたが今必要としているのは、この勝利を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。勝利の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1438, 143810, 14381002, '【AWA】のカードが現れました。供物・生贄を象徴し、無条件と見返りの違いを意識することを教えてくれます。結ばれる人との家族の未来において最も大切なのは、供物・生贄という概念を正しく認識することです。供物・生贄は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて供物・生贄と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、供物・生贄と共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1438, 143810, 14381003, '【I’O】のカードです。I’Oが示すのはミステリーであり、全ては必然で起こっていることの重要性です。恋愛における結ばれる人との家族の未来を占う上で、ミステリー以上に重要なものはありません。ミステリーは、二人の関係を支える基盤であり、愛を育む土壌です。このミステリーを大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、ミステリーがあれば必ず乗り越えられます。愛を信じ、ミステリーと共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1438, 143810, 14381004, '【POLI’AHU】のカードです。POLI’AHUが示すのは静けさの愛撫であり、不動の静けさからくる強さことの重要性です。あなたが知りたい結ばれる人との家族の未来の核心は、静けさの愛撫にあります。静けさの愛撫というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。静けさの愛撫を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。');
