INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1440, '職業倫理が知りたい！　働き手との職業的成長', '完全解明', 'キャリアパスへの道筋が見えてくる。職業への想いを軸にした仕事の充実を、ハワイアンタロットで鑑定。', 8, NULL, NULL, NULL, NULL, NULL, '2027-02-03 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1440, 144001, 'プロとしての道の行方'),
(1440, 144002, 'あなたの仕事の価値観とキャリアパス'),
(1440, 144003, '職業的成長への道筋'),
(1440, 144004, '働くあなたが望む職業への想い'),
(1440, 144005, 'プロとしての誇りから見える真実'),
(1440, 144006, 'キャリアの行方における実務者の役割');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1440, 144001, 14400101, '出たカードは【PAPA&WAKEA】。葛藤の中から新しいものを生み出すことを示す重要なカードです。キャリアや人生設計におけるプロとしての道の行方を考える際、苦闘・全体は最重要ファクターです。成功者に共通するのは、この苦闘・全体を深く理解し、実践していることです。苦闘・全体という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、苦闘・全体を磨き続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1440, 144001, 14400102, '【MANA】のカードが現れました。力を与えることを象徴し、満ちているエネルギーをどう使うかことを教えてくれます。キャリアや人生設計におけるプロとしての道の行方を考える際、力を与えることは最重要ファクターです。成功者に共通するのは、この力を与えることを深く理解し、実践していることです。力を与えることという軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、力を与えることを磨き続けてください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1440, 144001, 14400103, '【NALU】のカードが現れました。黙想を象徴し、感情を観察することを教えてくれます。プロとしての道の行方において、黙想が極めて重要な役割を果たしています。この黙想のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。黙想という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1440, 144001, 14400104, '【OLE】のカードです。OLEが示すのは物語であり、言葉を大切にすることの重要性です。プロとしての道の行方において、物語が極めて重要な役割を果たしています。この物語のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。物語という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1440, 144002, 14400201, '【HO’OPONOPONO】が選ばれました。「コミュニケーションを大切にする」がこのカードの核心的メッセージです。タロットが告げるあなたの仕事の価値観とキャリアパスに関する真実は、コミュニケーションに集約されています。コミュニケーションという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。コミュニケーションを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1440, 144002, 14400202, 'タロットが告げるのは【LEI】。大切というキーワードで、日々の暮らしを大切にことを伝えています。あなたの仕事の価値観とキャリアパスに関して、カードは大切という重要なメッセージを伝えています。あなたが今必要としているのは、この大切を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。大切の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1440, 144002, 14400203, '【LONO/KAMAPUA’A】のカードが現れました。収穫/豊穣を象徴し、やり切ることで収穫に繋がることを教えてくれます。カードが明かすあなたの仕事の価値観とキャリアパスの真実において、収穫/豊穣は中心的な役割を果たしています。収穫/豊穣を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。収穫/豊穣のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、収穫/豊穣という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1440, 144002, 14400204, '【MAMALAHOE】が選ばれました。「自分の気持ちを最優先する」がこのカードの核心的メッセージです。あなたの仕事の価値観とキャリアパスにおいて、同情が極めて重要な役割を果たしています。この同情のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。同情という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1440, 144003, 14400301, 'カードに現れたのは【HAUMEA】。テーマは「必要なものは与えられる」です。職業的成長への道筋に関して、カードは滋養という重要なメッセージを伝えています。あなたが今必要としているのは、この滋養を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。滋養の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1440, 144003, 14400302, '【KU/HINA】のカードが語りかけます。活動する/休息するという智恵を、あなたに授けようとしています。あなたが知りたい職業的成長への道筋の核心は、戦い/癒しにあります。戦い/癒しというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。戦い/癒しを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1440, 144003, 14400303, 'カードに現れたのは【ALOHA】。テーマは「真の愛を学ぶ」です。あなたが知りたい職業的成長への道筋の核心は、愛にあります。愛というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。愛を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1440, 144003, 14400304, '【PUHI】のカードが語りかけます。新しい創造のための破壊という智恵を、あなたに授けようとしています。職業的成長への道筋という人生の重要なテーマにおいて、崩壊は羅針盤となります。人生の岐路に立ったとき、崩壊を指針とすることで、正しい選択ができるでしょう。崩壊は、あなたの使命や天命とも深く関わっています。この崩壊を追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、崩壊に従って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1440, 144004, 14400401, 'ハワイアンタロットの【LAMAKU】。勝利のエネルギーを持ち、意思の力で喜びを手にすることを説いています。働くあなたが望む職業への想いにおいて最も大切なのは、勝利という概念を正しく認識することです。勝利は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて勝利と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、勝利と共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1440, 144004, 14400402, '【ANO’ANO】のカードが出ました。このカードは「思考が現実を創る／全てを受けとめる」を意味します。働くあなたが望む職業への想いにおいて最も大切なのは、意思という概念を正しく認識することです。意思は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて意思と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、意思と共に成長していきましょう。未来は明るく輝いています。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1440, 144004, 14400403, '【PELE】のカードです。PELEが示すのは隆起であり、情熱を注ぐことの重要性です。あなたが知りたい働くあなたが望む職業への想いの核心は、隆起にあります。隆起というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。隆起を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1440, 144004, 14400404, '【LAKA】のカードが現れました。インスピレーションを象徴し、直感を大切にすることを教えてくれます。キャリアや人生設計における働くあなたが望む職業への想いを考える際、インスピレーションは最重要ファクターです。成功者に共通するのは、このインスピレーションを深く理解し、実践していることです。インスピレーションという軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、インスピレーションを磨き続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1440, 144005, 14400501, '【HINA】のカードが現れました。養いを象徴し、自分自身を成長させることを教えてくれます。プロとしての誇りから見える真実を考える上で、養いは欠かせない要素となっています。養いの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、養いの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、養いのメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1440, 144005, 14400502, 'タロットが告げるのは【AWA】。供物・生贄というキーワードで、無条件と見返りの違いを意識することを伝えています。プロとしての誇りから見える真実を考える上で、供物・生贄は欠かせない要素となっています。供物・生贄の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、供物・生贄の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、供物・生贄のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1440, 144005, 14400503, '出たカードは【LEHUA】。安心して前進することを示す重要なカードです。あなたのプロとしての誇りから見える真実には、イニシエーションが深く関わっています。このイニシエーションというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。イニシエーションのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、イニシエーションと共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1440, 144005, 14400504, '【POLI’AHU】のカードが現れました。静けさの愛撫を象徴し、不動の静けさからくる強さことを教えてくれます。プロとしての誇りから見える真実において最も大切なのは、静けさの愛撫という概念を正しく認識することです。静けさの愛撫は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて静けさの愛撫と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、静けさの愛撫と共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1440, 144006, 14400601, '【ANUENUE】が選ばれました。「隠れた部分に光をあてる」がこのカードの核心的メッセージです。タロットが告げるキャリアの行方における実務者の役割に関する真実は、変容に集約されています。変容という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。変容を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1440, 144006, 14400602, '【HI’IAKA】のカードが現れました。献身を象徴し、望む結果をイメージすることを教えてくれます。キャリアや人生設計におけるキャリアの行方における実務者の役割を考える際、献身は最重要ファクターです。成功者に共通するのは、この献身を深く理解し、実践していることです。献身という軸をぶらさず、一貫して追求することで、あなたは望む成果を手にすることができるでしょう。焦らず、着実に、献身を磨き続けてください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1440, 144006, 14400603, '【PAHU】が選ばれました。「ターニングポイント」がこのカードの核心的メッセージです。タロットが告げるキャリアの行方における実務者の役割に関する真実は、タイミング・儀式に集約されています。タイミング・儀式という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。タイミング・儀式を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1440, 144006, 14400604, '【PUA】が選ばれました。「目的を定め前進あるのみ」がこのカードの核心的メッセージです。キャリアの行方における実務者の役割を考える上で、前進は欠かせない要素となっています。前進の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、前進の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、前進のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。');
