INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1443, '開ける未来の行方　二人の幸せ詳細鑑定', '完全解明', '彼の純粋な感情、そして今後の二人…　ハワイアンタロットが関係の安定の全てをお伝えします。', 1, NULL, NULL, NULL, NULL, NULL, '2027-02-06 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1443, 144301, 'パートナーが大切にすること'),
(1443, 144302, '率直な想いの意味と迎える明日'),
(1443, 144303, 'これからの展開の展望'),
(1443, 144304, '愛の形を左右する要因'),
(1443, 144305, '誠実な気持ちを深く知る'),
(1443, 144306, '永遠の絆の核心');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1443, 144301, 14430101, 'ハワイアンタロットの【MO’O】。罠のエネルギーを持ち、不安が成功を妨げることを説いています。あなたが知りたいパートナーが大切にすることの核心は、罠にあります。罠というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。罠を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1443, 144301, 14430102, '【KA WAI A KA PILILIKO】のカードが示されました。「ありのままを受け入れる」というメッセージが込められています。パートナーが大切にすることに対するハワイアンタロットの答えは明確です。それは反映です。この反映という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。反映は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で反映を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1443, 144301, 14430103, 'ハワイアンタロットの【AWA】。供物・生贄のエネルギーを持ち、無条件と見返りの違いを意識することを説いています。恋愛におけるパートナーが大切にすることを占う上で、供物・生贄以上に重要なものはありません。供物・生贄は、二人の関係を支える基盤であり、愛を育む土壌です。この供物・生贄を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、供物・生贄があれば必ず乗り越えられます。愛を信じ、供物・生贄と共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1443, 144301, 14430104, '出たカードは【PUHI】。新しい創造のための破壊ことを示す重要なカードです。パートナーが大切にすることに秘められた真実は、崩壊によって明らかになります。愛に関わるあらゆる疑問や不安は、崩壊という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、崩壊を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。崩壊さえ忘れなければ、二人の未来は明るく輝くでしょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1443, 144302, 14430201, '【HULU】のカードです。HULUが示すのはイリュージョンであり、無限の可能性ことの重要性です。あなたの率直な想いの意味と迎える明日には、イリュージョンが深く関わっています。このイリュージョンというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。イリュージョンのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、イリュージョンと共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1443, 144302, 14430202, '【KANALOA】のカードです。KANALOAが示すのは基盤であり、基盤づくりをすることの重要性です。カードが明かす率直な想いの意味と迎える明日の真実において、基盤は中心的な役割を果たしています。基盤を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。基盤のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、基盤という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1443, 144302, 14430203, 'カードに現れたのは【ALOHA】。テーマは「真の愛を学ぶ」です。率直な想いの意味と迎える明日において最も大切なのは、愛という概念を正しく認識することです。愛は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて愛と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、愛と共に成長していきましょう。未来は明るく輝いています。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1443, 144302, 14430204, '【PUA】が選ばれました。「目的を定め前進あるのみ」がこのカードの核心的メッセージです。恋愛における率直な想いの意味と迎える明日を占う上で、前進以上に重要なものはありません。前進は、二人の関係を支える基盤であり、愛を育む土壌です。この前進を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、前進があれば必ず乗り越えられます。愛を信じ、前進と共に歩んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1443, 144303, 14430301, 'ハワイアンタロットの【TI】。浄化のエネルギーを持ち、感情、肉体、精神の浄化をすることを説いています。これからの展開の展望を考える上で、浄化は欠かせない要素となっています。浄化の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、浄化の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、浄化のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1443, 144303, 14430302, '【NAUPAKA】のカードが語りかけます。切り離すという智恵を、あなたに授けようとしています。タロットが告げるこれからの展開の展望に関する真実は、別離に集約されています。別離という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。別離を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1443, 144303, 14430303, '【HUNA】のカードが出ました。このカードは「可能性を禁じない」を意味します。これからの展開の展望において最も大切なのは、秘密という概念を正しく認識することです。秘密は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて秘密と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、秘密と共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1443, 144303, 14430304, '出たカードは【LONO/KAMAPUA’A】。やり切ることで収穫に繋がることを示す重要なカードです。カードが示すこれからの展開の展望の答えは、収穫/豊穣にあります。この収穫/豊穣を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、収穫/豊穣という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、収穫/豊穣の導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1443, 144304, 14430401, '出たカードは【PAHU】。ターニングポイントことを示す重要なカードです。カードが示す愛の形を左右する要因の答えは、タイミング・儀式にあります。このタイミング・儀式を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、タイミング・儀式という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、タイミング・儀式の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1443, 144304, 14430402, '出たカードは【POLI’AHU】。不動の静けさからくる強さことを示す重要なカードです。カードが明かす愛の形を左右する要因の真実において、静けさの愛撫は中心的な役割を果たしています。静けさの愛撫を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。静けさの愛撫のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、静けさの愛撫という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1443, 144304, 14430403, '【MANA】のカードが語りかけます。満ちているエネルギーをどう使うかという智恵を、あなたに授けようとしています。あなたの愛の形を左右する要因には、力を与えることが深く関わっています。この力を与えることというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。力を与えることのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、力を与えることと共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1443, 144304, 14430404, '【ANUENUE】が選ばれました。「隠れた部分に光をあてる」がこのカードの核心的メッセージです。あなたの愛の形を左右する要因には、変容が深く関わっています。この変容というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。変容のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、変容と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1443, 144305, 14430501, 'カードに現れたのは【PAPA&WAKEA】。テーマは「葛藤の中から新しいものを生み出す」です。あなたが知りたい誠実な気持ちを深く知るの核心は、苦闘・全体にあります。苦闘・全体というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。苦闘・全体を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1443, 144305, 14430502, 'タロットが告げるのは【WA’A】。移動というキーワードで、大きな変化が訪れることを伝えています。誠実な気持ちを深く知るに秘められた真実は、移動によって明らかになります。愛に関わるあらゆる疑問や不安は、移動という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、移動を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。移動さえ忘れなければ、二人の未来は明るく輝くでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1443, 144305, 14430503, 'ハワイアンタロットの【MAMALAHOE】。同情のエネルギーを持ち、自分の気持ちを最優先することを説いています。誠実な気持ちを深く知るに関して、カードは同情という重要なメッセージを伝えています。あなたが今必要としているのは、この同情を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。同情の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1443, 144305, 14430504, 'カードに現れたのは【HINA】。テーマは「自分自身を成長させる」です。誠実な気持ちを深く知るを考える上で、養いは欠かせない要素となっています。養いの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、養いの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、養いのメッセージを受け取りましょう。道は必ず開けます。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1443, 144306, 14430601, '【OLE】が選ばれました。「言葉を大切にする」がこのカードの核心的メッセージです。永遠の絆の核心において最も大切なのは、物語という概念を正しく認識することです。物語は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて物語と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、物語と共に成長していきましょう。未来は明るく輝いています。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1443, 144306, 14430602, '【IWI】のカードが示されました。「自分も他人も大切にする」というメッセージが込められています。永遠の絆の核心に秘められた真実は、尊敬によって明らかになります。愛に関わるあらゆる疑問や不安は、尊敬という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、尊敬を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。尊敬さえ忘れなければ、二人の未来は明るく輝くでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1443, 144306, 14430603, '【PU’OLO】のカードが語りかけます。不必要なものを手放すという智恵を、あなたに授けようとしています。あなたが知りたい永遠の絆の核心の核心は、解放にあります。解放というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。解放を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1443, 144306, 14430604, 'ハワイアンタロットの【LEI】。大切のエネルギーを持ち、日々の暮らしを大切にことを説いています。カードが明かす永遠の絆の核心の真実において、大切は中心的な役割を果たしています。大切を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。大切のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、大切という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。');
