INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1445, '偽らざる思い深掘り　将来展望完全ガイド', 'あの方の真実', '本心からの想いが分からない…　先の動きが不安…　ハワイアンタロットが当の本人について詳しく占います。', 3, NULL, NULL, NULL, NULL, NULL, '2027-02-08 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1445, 144501, '今後の見込みを導く効果的な方法'),
(1445, 144502, '効果的な方法がもたらす先の展望'),
(1445, 144503, '未来の形について'),
(1445, 144504, '隠れた想いにおける想いの真意'),
(1445, 144505, '今後の推移におけるその人の役割'),
(1445, 144506, '気持ちの実態における正解の道');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1445, 144501, 14450101, '【MANO】が選ばれました。「リーダーシップを取る」がこのカードの核心的メッセージです。今後の見込みを導く効果的な方法を考える上で、リーダーシップは欠かせない要素となっています。リーダーシップの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、リーダーシップの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、リーダーシップのメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1445, 144501, 14450102, '【NALU】のカードが出ました。このカードは「感情を観察する」を意味します。カードが示す今後の見込みを導く効果的な方法の答えは、黙想にあります。この黙想を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、黙想という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、黙想の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1445, 144501, 14450103, '【HUNA】のカードが現れました。秘密を象徴し、可能性を禁じないことを教えてくれます。あなたが知りたい今後の見込みを導く効果的な方法の核心は、秘密にあります。秘密というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。秘密を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1445, 144501, 14450104, '【KU/HINA】のカードが示されました。「活動する/休息する」というメッセージが込められています。今後の見込みを導く効果的な方法において最も大切なのは、戦い/癒しという概念を正しく認識することです。戦い/癒しは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて戦い/癒しと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、戦い/癒しと共に成長していきましょう。未来は明るく輝いています。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1445, 144502, 14450201, '【MAMALAHOE】のカードが示されました。「自分の気持ちを最優先する」というメッセージが込められています。効果的な方法がもたらす先の展望において、同情が極めて重要な役割を果たしています。この同情のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。同情という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1445, 144502, 14450202, '【KA WAI A KA PILILIKO】が選ばれました。「ありのままを受け入れる」がこのカードの核心的メッセージです。タロットが告げる効果的な方法がもたらす先の展望に関する真実は、反映に集約されています。反映という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。反映を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1445, 144502, 14450203, 'ハワイアンタロットの【LAMAKU】。勝利のエネルギーを持ち、意思の力で喜びを手にすることを説いています。あなたの効果的な方法がもたらす先の展望には、勝利が深く関わっています。この勝利というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。勝利のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、勝利と共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1445, 144502, 14450204, '出たカードは【MO’O】。不安が成功を妨げることを示す重要なカードです。タロットが告げる効果的な方法がもたらす先の展望に関する真実は、罠に集約されています。罠という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。罠を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1445, 144503, 14450301, 'タロットが告げるのは【KANE】。創造というキーワードで、もう一度始めることを伝えています。カードが明かす未来の形についての真実において、創造は中心的な役割を果たしています。創造を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。創造のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、創造という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1445, 144503, 14450302, '【LONO/KAMAPUA’A】のカードです。LONO/KAMAPUA’Aが示すのは収穫/豊穣であり、やり切ることで収穫に繋がることの重要性です。カードが示す未来の形についての答えは、収穫/豊穣にあります。この収穫/豊穣を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、収穫/豊穣という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、収穫/豊穣の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1445, 144503, 14450303, '【PU’OLO】のカードです。PU’OLOが示すのは解放であり、不必要なものを手放すことの重要性です。未来の形についてを考える上で、解放は欠かせない要素となっています。解放の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、解放の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、解放のメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1445, 144503, 14450304, '【PAPA&WAKEA】のカードです。PAPA&WAKEAが示すのは苦闘・全体であり、葛藤の中から新しいものを生み出すことの重要性です。あなたの未来の形についてには、苦闘・全体が深く関わっています。この苦闘・全体というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。苦闘・全体のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、苦闘・全体と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1445, 144504, 14450401, '【PAHU】のカードが語りかけます。ターニングポイントという智恵を、あなたに授けようとしています。あなたが知りたい隠れた想いにおける想いの真意の核心は、タイミング・儀式にあります。タイミング・儀式というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。タイミング・儀式を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1445, 144504, 14450402, '【HI’IAKA】のカードが語りかけます。望む結果をイメージするという智恵を、あなたに授けようとしています。カードが示す隠れた想いにおける想いの真意の答えは、献身にあります。この献身を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、献身という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、献身の導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1445, 144504, 14450403, '出たカードは【TI】。感情、肉体、精神の浄化をすることを示す重要なカードです。カードが明かす隠れた想いにおける想いの真意の真実において、浄化は中心的な役割を果たしています。浄化を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。浄化のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、浄化という贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1445, 144504, 14450404, '【WA’A】のカードが語りかけます。大きな変化が訪れるという智恵を、あなたに授けようとしています。あなたが知りたい隠れた想いにおける想いの真意の核心は、移動にあります。移動というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。移動を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1445, 144505, 14450501, '出たカードは【ALOHA】。真の愛を学ぶことを示す重要なカードです。カードが明かす今後の推移におけるその人の役割の真実において、愛は中心的な役割を果たしています。愛を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。愛のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、愛という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1445, 144505, 14450502, 'ハワイアンタロットの【OLE】。物語のエネルギーを持ち、言葉を大切にすることを説いています。今後の推移におけるその人の役割において最も大切なのは、物語という概念を正しく認識することです。物語は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて物語と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、物語と共に成長していきましょう。未来は明るく輝いています。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1445, 144505, 14450503, '【LEHUA】が選ばれました。「安心して前進する」がこのカードの核心的メッセージです。今後の推移におけるその人の役割に対するハワイアンタロットの答えは明確です。それはイニシエーションです。このイニシエーションという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。イニシエーションは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でイニシエーションを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1445, 144505, 14450504, '出たカードは【LEI】。日々の暮らしを大切にことを示す重要なカードです。今後の推移におけるその人の役割において、大切が極めて重要な役割を果たしています。この大切のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。大切という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1445, 144506, 14450601, 'タロットが告げるのは【HINA】。養いというキーワードで、自分自身を成長させることを伝えています。あなたが知りたい気持ちの実態における正解の道の核心は、養いにあります。養いというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。養いを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1445, 144506, 14450602, 'カードに現れたのは【MANA】。テーマは「満ちているエネルギーをどう使うか」です。タロットが告げる気持ちの実態における正解の道に関する真実は、力を与えることに集約されています。力を与えることという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。力を与えることを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1445, 144506, 14450603, 'カードに現れたのは【ANO’ANO】。テーマは「思考が現実を創る／全てを受けとめる」です。あなたの気持ちの実態における正解の道には、意思が深く関わっています。この意思というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。意思のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、意思と共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1445, 144506, 14450604, '出たカードは【POLI’AHU】。不動の静けさからくる強さことを示す重要なカードです。タロットが告げる気持ちの実態における正解の道に関する真実は、静けさの愛撫に集約されています。静けさの愛撫という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。静けさの愛撫を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。');
