INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1450, 145001, '職業倫理が示すもの'),
(1450, 145002, 'プロとしてのあなたが大切にすること'),
(1450, 145003, 'キャリアの展開におけるビジネス運'),
(1450, 145004, '従事者の視点から見たプロとしての誇り'),
(1450, 145005, 'ビジネスパーソンとの職業的方向性'),
