INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1453, 'かけがえのない人の魂の叫びと関係の方向性　徹底解明', '二人の軌跡の答え', '大切な人との関係の安定が明らかに。秘めた思いと関係の方向性を、ハワイアンタロットが徹底的に占います。', 1, NULL, NULL, NULL, NULL, NULL, '2027-02-16 00:00:00');
